INSERT INTO scale_test_configs (id, test_type, short_name, percentiles, created_at, updated_at, name, action_plan, dimensions, recommendations) VALUES ('023e7a06-f1b3-4650-88e8-4d42df3a339d', 'psychological_resilience', 'RES', '{10,25,50,85,98}', NOW(), NOW(), '{"en-US": "Psychological Resilience Test", "zh-CN": "心理韧性测试", "de-DE": "Psychologischer Resilienztest", "fr-FR": "Test de Résilience Psychologique", "zh-TW": "心理韌性測試"}', '{"en-US": ["Week 1: Identify top 3 recurring stressors and one controllable action for each", "Week 2: Practice emotion naming + 10 minutes mindfulness on 4 days", "Week 3: Do one planned moderate challenge and write a brief resilience reflection", "Week 4: Build/update your resilience resource list and schedule one support conversation"], "zh-CN": ["第1周：识别3个反复出现的压力源，并为每个写下1个可控行动", "第2周：练习情绪命名 + 正念10分钟（每周至少4天）", "第3周：完成一次“计划中的适度挑战”，并写下韧性复盘", "第4周：整理/更新个人“韧性资源清单”，安排一次支持性沟通"], "de-DE": ["Woche 1: Identifizieren Sie die 3 häufigsten Stressfaktoren und eine kontrollierbare Aktion für jeden", "Woche 2: Üben Sie das Benennen von Emotionen + 10 Minuten Achtsamkeit an 4 Tagen", "Woche 3: Machen Sie eine geplante moderate Herausforderung und schreiben Sie eine kurze Resilienz-Reflexion", "Woche 4: Erstellen/aktualisieren Sie Ihre Resilienz-Ressourcenliste und planen Sie ein Unterstützungs-Gespräch"], "fr-FR": ["Semaine 1 : Identifiez les 3 principaux facteurs de stress récurrents et une action contrôlable pour chacun", "Semaine 2 : Pratiquez la nomination des émotions + 10 minutes de pleine conscience 4 jours par semaine", "Semaine 3 : Relevez un défi modéré planifié et rédigez une réflexion brève sur votre résilience", "Semaine 4 : Construisez/actualisez votre liste de ressources de résilience et planifiez une conversation de soutien"], "zh-TW": ["第1週：識別3個反覆出現的壓力源，並為每個寫下1個可控行動", "第2週：練習情緒命名 + 正念10分鐘（每週至少4天）", "第3週：完成一次“計劃中的適度挑戰”，並寫下韌性復盤", "第4週：整理/更新個人“韌性資源清單”，安排一次支持性溝通"]}', '{"en-US": {"Emotional regulation": 70, "Cognitive flexibility": 65, "Social support utilization": 62, "Problem-solving adaptability": 68}, "zh-CN": {"情绪调节": 70, "认知灵活性": 65, "社会支持运用": 62, "适应性问题解决": 68}, "de-DE": {"Emotionale Regulierung": 70, "Kognitive Flexibilität": 65, "Nutzung sozialer Unterstützung": 62, "Problemlösungsanpassungsfähigkeit": 68}, "fr-FR": {"Régulation émotionnelle": 70, "Flexibilité cognitive": 65, "Utilisation du soutien social": 62, "Adaptabilité à la résolution de problèmes": 68}, "zh-TW": {"情緒調節": 70, "認知靈活性": 65, "社會支持運用": 62, "適應性問題解決": 68}}', '{"en-US": ["Start with small challenges, gradually building coping capacity", "Learn cognitive restructuring to change perspectives on setbacks", "Establish stable support system, know who to turn to in difficulty", "Practice self-compassion, avoid excessive self-criticism", "Set small, achievable goals, gradually building success experiences"], "zh-CN": ["从小挑战开始，逐步建立应对能力", "学习“认知重构”技巧，改变对挫折的看法", "建立稳定的支持系统，知道在困难时向谁求助", "练习自我同情，避免过度自我批评", "设定小而可实现的目标，逐步建立成功经验"], "de-DE": ["Beginnen Sie mit kleinen Herausforderungen, um die Bewältigungsfähigkeit schrittweise aufzubauen", "Lernen Sie kognitive Umstrukturierung, um Perspektiven auf Rückschläge zu ändern", "Etablieren Sie ein stabiles Unterstützungssystem, wissen Sie, an wen Sie sich in schwierigen Zeiten wenden können", "Üben Sie Selbstmitgefühl, vermeiden Sie übermäßige Selbstkritik", "Setzen Sie kleine, erreichbare Ziele, um schrittweise Erfolgserfahrungen aufzubauen"], "fr-FR": ["Commencez par de petits défis pour développer progressivement votre capacité d''adaptation", "Apprenez la restructuration cognitive pour changer de perspective sur les revers", "Établissez un système de soutien stable, sachez à qui vous adresser en cas de difficulté", "Pratiquez l''auto-compassion, évitez les critiques excessives envers vous-même", "Fixez des objectifs petits et réalisables pour construire progressivement des expériences de réussite"], "zh-TW": ["從小挑戰開始，逐步建立應對能力", "學習“認知重構”技巧，改變對挫折的看法", "建立穩定的支持系統，知道在困難時向誰求助", "練習自我同情，避免過度自我批評", 