INSERT INTO scale_test_configs (id, test_type, short_name, percentiles, created_at, updated_at, name, action_plan, dimensions, recommendations) VALUES ('394091fa-2b0b-4435-a161-82cc313f2ae4', 'emotional_recognition', 'EQ', '{10,25,50,85,98}', NOW(), NOW(), '{"en-US": "Emotional Recognition Test", "zh-CN": "情绪识别能力测试", "de-DE": "Emotionserkennungstest", "fr-FR": "Test de reconnaissance émotionnelle", "zh-TW": "情緒識別能力測試"}', '{"en-US": ["Week 1: Log 3 daily cases of successful/unsuccessful emotion recognition", "Week 2: Practice identifying emotions from tone (calls/voice messages)", "Week 3: Practice identifying mixed emotions (e.g., bittersweet)", "Week 4: Apply emotional recognition to improve one important conversation"], "zh-CN": ["第一周：每天记录3次情绪识别成功/失败的案例并简要复盘", "第二周：练习从声音语调中识别情绪（电话、语音消息）", "第三周：练习识别复杂/混合情绪（如悲喜交加）", "第四周：将情绪识别能力应用于改善一次重要沟通"], "de-DE": ["Woche 1: Protokollieren Sie täglich 3 Fälle von erfolgreicher/erfolgloser Emotionserkennung", "Woche 2: Üben Sie, Emotionen anhand des Tons zu erkennen (Anrufe/Sprachnachrichten)", "Woche 3: Üben Sie, gemischte Emotionen zu erkennen (z. B. bittersüß)", "Woche 4: Wenden Sie die Emotionserkennung an, um ein wichtiges Gespräch zu verbessern"], "fr-FR": ["Semaine 1 : Notez 3 cas quotidiens de reconnaissance émotionnelle réussie/échouée", "Semaine 2 : Pratiquez l''identification des émotions à partir du ton (appels/messages vocaux)", "Semaine 3 : Pratiquez l''identification des émotions mixtes (par ex., douce-amère)", "Semaine 4 : Appliquez la reconnaissance émotionnelle pour améliorer une conversation importante"], "zh-TW": ["第一週：每天記錄3次情緒識別成功/失敗的案例並簡要復盤", "第二週：練習從聲音語調中識別情緒（電話、語音消息）", "第三週：練習識別複雜/混合情緒（如悲喜交加）", "第四週：將情緒識別能力應用於改善一次重要溝通"]}', '{"en-US": {"Tone emotion perception": 75, "Body language interpretation": 70, "Facial expression recognition": 85, "Sensitivity to emotional changes": 80}, "zh-CN": {"肢体语言解读": 70, "语调情绪感知": 75, "面部表情识别": 85, "情绪变化敏感度": 80}, "de-DE": {"Emotionserkennung im Ton": 75, "Interpretation der Körpersprache": 70, "Erkennung von Gesichtsausdrücken": 85, "Sensibilität für emotionale Veränderungen": 80}, "fr-FR": {"Perception des émotions dans le ton": 75, "Interprétation du langage corporel": 70, "Reconnaissance des expressions faciales": 85, "Sensibilité aux changements émotionnels": 80}, "zh-TW": {"語調情緒感知": 75, "肢體語言解讀": 70, "面部表情識別": 85, "情緒變化敏感度": 80}}', '{"en-US": ["Observe 5 people''s facial expressions daily, attempting to guess their emotional states", "Watch movies with sound off, identifying character emotions through visuals only", "Practice actively asking about feelings: ''How are you feeling about...?''", "Download emotional recognition training apps, practice 10 minutes daily"], "zh-CN": ["每天观察5个人的面部表情，尝试猜测他们的情绪状态", "观看电影时关闭声音，仅通过画面识别角色情绪", "练习主动询问他人感受：''你现在的感受是...？''", "下载情绪识别训练App，每天练习10分钟"], "de-DE": ["Beobachten Sie täglich die Gesichtsausdrücke von 5 Personen und versuchen Sie, ihre emotionalen Zustände zu erraten", "Schauen Sie Filme ohne Ton und identifizieren Sie die Emotionen der Charaktere nur durch visuelle Hinweise", "Üben Sie, aktiv nach Gefühlen zu fragen: ''Wie fühlen Sie sich in Bezug auf...?''", "Laden Sie Apps zur Emotionserkennung herunter und üben Sie täglich 10 Minuten"], "fr-FR": ["Observez quotidiennement les expressions faciales de 5 personnes et tentez de deviner leurs états émotionnels", "Regardez des films sans son, en identifiant les émotions des personnages uniquement par les visuels", "Pratiquez le fait de demander activement des ressentis : ''Comment vous sentez-vous à propos de... ?''", "Téléchargez des applications d''entraînement à la reconnaissance émotionnelle, pratiquez 10 minutes par jour"], "zh-TW": ["每天觀察5個人的面部表情，嘗試猜測他們的情緒狀態", "觀看電影時關閉聲音，僅通過畫面識別角色情緒", "練習主動詢問他人感受：''你現在的感受是...？''", "下載情緒識別訓練App，每天練習10分鐘"]}');
INSERT INTO scale_test_configs (id, test_type, short_name, percentiles, created_at, updated_at, name, action_plan, dimensions, recommendations) VALUES ('56ce0228-5d36-4ea3-a84e-b60da4325823', 'life_satisfaction', 'SWLS', '{10,25,50,85,98}', NOW(), NOW(), '{"zh-CN": "生活满意度量表", "en-US": "Life Satisfaction Scale", "de-DE": "Lebenszufriedenheitsskala", "fr-FR": "Échelle de satisfaction de vie", "zh-TW": "生活滿意度量表"}', '{"zh-CN": ["第一周：完成生活领域满意度评估，选出最优先的1-2个方向", "第二周：制定可衡量的改进步骤，并进行每日追踪", "第三周：增加积极活动，减少消耗能量的负面活动", "第四周：复盘进展、调整目标并规划下个月行动"], "en-US": ["Week 1: Do a life domain satisfaction assessment and pick top 1-2 priorities", "Week 2: Create measurable improvement steps and track daily", "Week 3: Increase positive activities and reduce energy-draining ones", "Week 4: Review progress, adjust goals, and plan next month"], "de-DE": ["Woche 1: Führen Sie eine Zufriedenheitsbewertung der Lebensbereiche durch und wählen Sie die 1-2 wichtigsten Prioritäten aus", "Woche 2: Erstellen Sie messbare Verbesserungsmaßnahmen und verfolgen Sie diese täglich", "Woche 3: Erhöhen Sie positive Aktivitäten und reduzieren Sie energiezehrende", "Woche 4: Überprüfen Sie den Fortschritt, passen Sie die Ziele an und planen Sie den nächsten Monat"], "fr-FR": ["Semaine 1 : Faites une évaluation de la satisfaction des domaines de vie et choisissez les 1-2 priorités principales", "Semaine 2 : Créez des étapes d''amélioration mesurables et suivez-les quotidiennement", "Semaine 3 : Augmentez les activités positives et réduisez celles qui drainent de l''énergie", "Semaine 4 : Passez en revue les progrès, ajustez les objectifs et planifiez le mois suivant"], "zh-TW": ["第一週：完成生活領域滿意度評估，選出最優先的1-2個方向", "第二週：制定可衡量的改進步驟，並進行每日追蹤", "第三週：增加積極活動，減少消耗能量的負面活動", "第四週：復盤進展、調整目標並規劃下個月行動"]}', '{"zh-CN": {"关系满意度": 62, "情绪积极性": 60, "生活意义感": 65, "成就与掌控感": 64}, "en-US": {"Meaning in life": 65, "Positive affect": 60, "Achievement & control": 64, "Relationship satisfaction": 62}, "de-DE": {"Lebenssinn": 65, "Positive Stimmung": 60, "Leistung & Kontrolle": 64, "Beziehungszufriedenheit": 62}, "fr-FR": {"Sens de la vie": 65, "Affect positif": 60, "Réussite & contrôle": 64, "Satisfaction relationnelle": 62}, "zh-TW": {"生活意義感": 65, "情緒積極性": 60, "成就與掌控感": 64, "關係滿意度": 62}}', '{"zh-CN": ["识别最不满意的领域（工作、关系、健康等）", "设定一个小的、可实现的改进目标", "每天记录三件感恩的事情", "探索生活意义和价值观，重新评估优先事项", "考虑与生活教练或心理咨询师交流"], "en-US": ["Identify most dissatisfied areas (work, relationships, health, etc.)", "Set one small, achievable improvement goal", "Record three grateful things daily", "Explore life meaning and values, reassess priorities", "Consider talking with life coach or counselor"], "de-DE": ["Identifizieren Sie die am wenigsten zufriedenstellenden Bereiche (Arbeit, Beziehungen, Gesundheit usw.)", "Setzen Sie ein kleines, erreichbares Verbesserungsziel", "Notieren Sie täglich drei Dinge, für die Sie dankbar sind", "Erforschen Sie den Lebenssinn und die Werte und bewerten Sie die Prioritäten neu", "Erwägen Sie Gespräche mit einem Lebenscoach oder Berater"], "fr-FR": ["Identifiez les domaines les moins satisfaisants (travail, relations, santé, etc.)", "Fixez un petit objectif d''amélioration réalisable", "Notez trois choses pour lesquelles vous êtes reconnaissant chaque jour", "Explorez le sens de la vie et les valeurs, réévaluez les priorités", "Envisagez de parler avec un coach de vie ou un conseiller"], "zh-TW": ["識別最不滿意的領域（工作、關係、健康等）", "設定一個小的、可實現的改進目標", "每天記錄三件感恩的事情", "探索生活意義和價值觀，重新評估優先事項", "考慮與生活教練或心理諮詢師交流"]}');
INSERT INTO scale_test_configs (id, test_type, short_name, percentiles, created_at, updated_at, name, action_plan, dimensions, recommendations) VALUES ('7f99d78d-2529-4eaa-94ed-c2617dc1ccda', 'stress_index', 'STR', '{85,70,50,25,5}', NOW(), NOW(), '{"en-US": "Stress Index Self-Assessment", "zh-CN": "压力指数自我评估", "de-DE": "Stressindex-Selbstbewertung", "fr-FR": "Auto-évaluation de l''indice de stress", "zh-TW": "壓力指數自我評估"}', '{"en-US": ["Week 1: Practice mindfulness meditation 10 minutes daily", "Week 2: Identify and record main stressors", "Week 3: Implement one stress management strategy (e.g., time management)", "Week 4: Evaluate stress changes and adjust coping strategies"], "zh-CN": ["第1周：每天练习10分钟正念冥想", "第2周：识别并记录主要压力源", "第3周：实施一种压力管理策略（例如时间管理）", "第4周：评估压力变化并调整应对策略"], "de-DE": ["Woche 1: Täglich 10 Minuten Achtsamkeitsmeditation üben", "Woche 2: Hauptstressoren identifizieren und aufzeichnen", "Woche 3: Eine Stressbewältigungsstrategie umsetzen (z. B. Zeitmanagement)", "Woche 4: Stressveränderungen bewerten und Bewältigungsstrategien anpassen"], "fr-FR": ["Semaine 1 : Pratiquer la méditation de pleine conscience 10 minutes par jour", "Semaine 2 : Identifier et enregistrer les principaux facteurs de stress", "Semaine 3 : Mettre en œuvre une stratégie de gestion du stress (par ex. : gestion du temps)", "Semaine 4 : Évaluer les changements de stress et ajuster les stratégies d''adaptation"], "zh-TW": ["第1週：每天練習10分鐘正念冥想", "第2週：識別並記錄主要壓力源", "第3週：實施一種壓力管理策略（例如時間管理）", "第4週：評估壓力變化並調整應對策略"]}', '{"en-US": {"Life stress": 55, "Work stress": 65, "Emotional stress": 60, "Physical stress response": 50}, "zh-CN": {"生活压力": 55, "工作压力": 65, "情绪压力": 60, "身体压力反应": 50}, "de-DE": {"Lebensstress": 55, "Arbeitsstress": 65, "Emotionaler Stress": 60, "Physische Stressreaktion": 50}, "fr-FR": {"Stress de la vie": 55, "Stress au travail": 65, "Stress émotionnel": 60, "Réponse physique au stress": 50}, "zh-TW": {"生活壓力": 55, "工作壓力": 65, "情緒壓力": 60, "身體壓力反應": 50}}', '{"en-US": ["Maintain healthy habits and balanced lifestyle", "Establish preventive mechanisms for potential future stress", "Use low-stress periods to develop skills and interests", "Help friends or family with higher stress levels"], "zh-CN": ["保持健康的习惯和平衡的生活方式", "建立预防未来潜在压力的机制", "利用低压力时期发展技能和兴趣", "帮助压力较大的朋友或家人"], "de-DE": ["Gesunde Gewohnheiten und einen ausgewogenen Lebensstil beibehalten", "Präventive Mechanismen für potenziellen zukünftigen Stress etablieren", "Stressarme Phasen nutzen, um Fähigkeiten und Interessen zu entwickeln", "Freunden oder Familienmitgliedern mit höherem Stress helfen"], "fr-FR": ["Maintenir des habitudes saines et un mode de vie équilibré", "Établir des mécanismes préventifs pour le stress potentiel futur", "Utiliser les périodes de faible stress pour développer des compétences et des intérêts", "Aider les amis ou la famille ayant des niveaux de stress plus élevés"], "zh-TW": ["保持健康的習慣和平衡的生活方式", "建立預防未來潛在壓力的機制", "利用低壓力時期發展技能和興趣", "幫助壓力較大的朋友或家人"]}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('13036127-b76f-4ad1-a6df-7d080721ab31', 'life_satisfaction', '1', '20', '36', '#FF6B6B', NOW(), NOW(), '{"en-US": {"Meaning in life": 35, "Positive affect": 30, "Achievement & control": 32, "Relationship satisfaction": 40}, "zh-CN": {"关系满意度": 40, "情绪积极性": 30, "生活意义感": 35, "成就与掌控感": 32}, "de-DE": {"Lebenssinn": 35, "Positive Stimmung": 30, "Leistung & Kontrolle": 32, "Beziehungszufriedenheit": 40}, "fr-FR": {"Sens de la vie": 35, "Affect positif": 30, "Réussite et contrôle": 32, "Satisfaction relationnelle": 40}, "zh-TW": {"生活意義感": 35, "正向情緒": 30, "成就與掌控感": 32, "關係滿意度": 40}}', '{"en-US": "Very Dissatisfied", "zh-CN": "非常不满意", "de-DE": "Sehr unzufrieden", "fr-FR": "Très insatisfait", "zh-TW": "非常不滿意"}', '{"en-US": "You''re dissatisfied with multiple aspects of current life, possibly feeling life lacks meaning, direction, or sources of happiness.", "zh-CN": "您对目前生活的多个方面感到不满意，可能感到生活缺乏意义、方向或快乐来源。", "de-DE": "Sie sind mit mehreren Aspekten Ihres aktuellen Lebens unzufrieden und haben möglicherweise das Gefühl, dass Ihrem Leben Sinn, Richtung oder Quellen des Glücks fehlen.", "fr-FR": "Vous êtes insatisfait de plusieurs aspects de votre vie actuelle, ressentant peut-être un manque de sens, de direction ou de sources de bonheur.", "zh-TW": "您對目前生活的多個方面感到不滿意，可能感到生活缺乏意義、方向或快樂來源。"}', '{"en-US": "Your current life satisfaction is low, possibly affecting overall happiness and motivation. Importantly, this state can change. Important note: Very low life satisfaction may correlate with depression or other mental health issues. If lasting over two weeks, consider professional help.", "zh-CN": "您对当前生活的满意度较低，这可能影响您的整体幸福感和动力。重要的是，这种状态是可以改变的。重要说明：极低的生活满意度可能与抑郁或其他心理健康问题相关。如果这种状态持续超过两周，请考虑寻求专业帮助。", "de-DE": "Ihre aktuelle Lebenszufriedenheit ist gering, was möglicherweise Ihre allgemeine Glücklichkeit und Motivation beeinträchtigt. Wichtig: Dieser Zustand kann sich ändern. Wichtiger Hinweis: Sehr geringe Lebenszufriedenheit kann mit Depressionen oder anderen psychischen Gesundheitsproblemen zusammenhängen. Wenn dies länger als zwei Wochen anhält, ziehen Sie professionelle Hilfe in Betracht.", "fr-FR": "Votre satisfaction actuelle de vie est faible, ce qui peut affecter votre bonheur et votre motivation globale. Il est important de noter que cet état peut changer. Note importante : Une satisfaction de vie très faible peut être liée à la dépression ou à d''autres problèmes de santé mentale. Si cela dure plus de deux semaines, envisagez de demander une aide professionnelle.", "zh-TW": "您對目前生活的滿意度較低，這可能影響您的整體幸福感和動力。重要的是，這種狀態是可以改變的。重要說明：極低的生活滿意度可能與憂鬱或其他心理健康問題相關。如果這種狀態持續超過兩週，請考慮尋求專業幫助。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('1896fba2-896e-4808-8fd1-ab86f2edfd75', 'leadership_potential', '3', '53', '68', '#42A5F5', NOW(), NOW(), '{"en-US": {"Goal & execution": 68, "Team collaboration": 62, "Decision & ownership": 60, "Communication & influence": 65}, "zh-CN": {"团队协作": 62, "决策与担当": 60, "沟通影响力": 65, "目标与执行": 68}, "de-DE": {"Ziel & Ausführung": 68, "Teamzusammenarbeit": 62, "Entscheidungen & Verantwortung": 60, "Kommunikation & Einfluss": 65}, "fr-FR": {"Objectif & exécution": 68, "Collaboration en équipe": 62, "Décision & responsabilité": 60, "Communication & influence": 65}, "zh-TW": {"目標與執行": 68, "團隊協作": 62, "決策與擔當": 60, "溝通與影響力": 65}}', '{"en-US": "Moderate Potential", "zh-CN": "中等潜力", "de-DE": "Mittleres Potenzial", "fr-FR": "Potentiel modéré", "zh-TW": "中等潛力"}', '{"en-US": "You have moderate leadership potential with basic qualities to become effective leader, possibly showing leadership in specific situations or fields.", "zh-CN": "您具有中等领导潜力，具备成为有效领导者的基本素质，可能在特定情境或领域表现出领导能力。", "de-DE": "Sie haben ein mittleres Führungspotenzial mit grundlegenden Qualitäten, um ein effektiver Führer zu werden, und zeigen möglicherweise Führungsqualitäten in bestimmten Situationen oder Bereichen.", "fr-FR": "Vous avez un potentiel de leadership modéré avec des qualités de base pour devenir un leader efficace, pouvant montrer des capacités de leadership dans des situations ou domaines spécifiques.", "zh-TW": "您具有中等領導潛力，具備成為有效領導者的基本素質，可能在特定情境或領域展現出領導能力。"}', '{"en-US": "You''ve demonstrated foundation for becoming effective leader. Further developing leadership skills and style can yield better results in formal or informal leadership roles. Important note: Moderate leadership potential may suffice in stable environments but need enhancement in rapidly changing or crisis situations. Select development focus based on career goals.", "zh-CN": "您已经展现出成为有效领导者的基础。进一步发展您的领导技能和风格，可以在正式或非正式领导角色中取得更好成果。重要说明：中等领导潜力在稳定环境中可能足够，但在快速变化或危机情境中可能需要更高级的领导能力。根据职业目标选择发展重点。", "de-DE": "Sie haben die Grundlage für eine effektive Führung gezeigt. Die Weiterentwicklung von Führungsfähigkeiten und -stil kann bessere Ergebnisse in formellen oder informellen Führungsrollen bringen. Wichtiger Hinweis: Mittleres Führungspotenzial kann in stabilen Umgebungen ausreichen, erfordert jedoch Verbesserungen in schnelllebigen oder Krisensituationen. Wählen Sie den Entwicklungsschwerpunkt basierend auf Ihren Karrierezielen.", "fr-FR": "Vous avez démontré une base pour devenir un leader efficace. Développer davantage vos compétences et votre style de leadership peut donner de meilleurs résultats dans des rôles de leadership formels ou informels. Note importante : Un potentiel de leadership modéré peut suffire dans des environnements stables mais nécessite des améliorations dans des situations de crise ou de changement rapide. Choisissez votre axe de développement en fonction de vos objectifs de carrière.", "zh-TW": "您已展現出成為有效領導者的基礎。進一步發展您的領導技能和風格，可以在正式或非正式的領導角色中取得更好的成果。重要說明：中等領導潛力在穩定環境中可能足夠，但在快速變化或危機情境中可能需要更高級的領導能力。根據職業目標選擇發展重點。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('1df13f7d-397c-4c7f-ad5c-f93f360d885d', 'life_satisfaction', '4', '69', '84', '#66BB6A', NOW(), NOW(), '{"zh-CN": {"关系满意度": 78, "情绪积极性": 75, "生活意义感": 80, "成就与掌控感": 82}, "en-US": {"Meaning in life": 80, "Positive affect": 75, "Achievement & control": 82, "Relationship satisfaction": 78}, "de-DE": {"Lebenssinn": 80, "Positive Stimmung": 75, "Leistung & Kontrolle": 82, "Beziehungszufriedenheit": 78}, "fr-FR": {"Sens de la vie": 80, "Affect positif": 75, "Réussite et contrôle": 82, "Satisfaction relationnelle": 78}, "zh-TW": {"生活意義感": 80, "正向情緒": 75, "成就與掌控感": 82, "關係滿意度": 78}}', '{"zh-CN": "相当满意", "en-US": "Quite Satisfied", "de-DE": "Ziemlich zufrieden", "fr-FR": "Assez satisfait", "zh-TW": "相當滿意"}', '{"zh-CN": "您对生活相当满意，在多个重要领域感到满足和有意义，整体生活质量较高。", "en-US": "You''re quite satisfied with life, feeling content and meaningful in multiple important areas with overall high life quality.", "de-DE": "Sie sind mit Ihrem Leben ziemlich zufrieden, fühlen sich in mehreren wichtigen Bereichen zufrieden und sinnvoll, mit insgesamt hoher Lebensqualität.", "fr-FR": "Vous êtes assez satisfait de votre vie, vous vous sentez épanoui et trouvez du sens dans plusieurs domaines importants, avec une qualité de vie globale élevée.", "zh-TW": "您對生活相當滿意，在多個重要領域感到滿足和有意義，整體生活品質較高。"}', '{"zh-CN": "您对生活有较高的满意度，这表明您的生活安排与您的价值观和需求基本一致。继续保持这种状态是值得的。重要说明：高生活满意度有时可能降低对他人痛苦的同理心。注意保持谦逊和对他人的关怀。", "en-US": "You have relatively high life satisfaction, suggesting your life arrangements align well with values and needs. Maintaining this state is worthwhile. Important note: High satisfaction may sometimes reduce empathy for others'' suffering. Maintain humility and care for others.", "de-DE": "Sie haben eine relativ hohe Lebenszufriedenheit, was darauf hindeutet, dass Ihre Lebensgestaltung gut mit Ihren Werten und Bedürfnissen übereinstimmt. Es lohnt sich, diesen Zustand beizubehalten. Wichtiger Hinweis: Hohe Zufriedenheit kann manchmal die Empathie für das Leid anderer verringern. Bewahren Sie Demut und Fürsorge für andere.", "fr-FR": "Vous avez un niveau de satisfaction de vie relativement élevé, ce qui suggère que vos choix de vie sont en adéquation avec vos valeurs et besoins. Maintenir cet état est souhaitable. Note importante : Une satisfaction élevée peut parfois réduire l''empathie envers la souffrance des autres. Gardez de l''humilité et prenez soin des autres.", "zh-TW": "您對生活有較高的滿意度，這表明您的生活安排與您的價值觀和需求基本一致。繼續保持這種狀態是值得的。重要說明：高生活滿意度有時可能降低對他人痛苦的同理心。注意保持謙遜和對他人的關懷。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('251ad8e3-24a1-4220-a403-03887ff10246', 'leadership_potential', '1', '20', '36', '#FF6B6B', NOW(), NOW(), '{"en-US": {"Goal & execution": 40, "Team collaboration": 38, "Decision & ownership": 32, "Communication & influence": 35}, "zh-CN": {"团队协作": 38, "决策与担当": 32, "沟通影响力": 35, "目标与执行": 40}, "de-DE": {"Ziel & Ausführung": 40, "Teamzusammenarbeit": 38, "Entscheidung & Verantwortung": 32, "Kommunikation & Einfluss": 35}, "fr-FR": {"Objectifs & exécution": 40, "Collaboration en équipe": 38, "Décision & responsabilité": 32, "Communication & influence": 35}, "zh-TW": {"目標與執行": 40, "團隊協作": 38, "決策與擔當": 32, "溝通影響力": 35}}', '{"en-US": "Foundational Potential", "zh-CN": "基础潜力", "de-DE": "Grundlegendes Potenzial", "fr-FR": "Potentiel Fondamental", "zh-TW": "基礎潛力"}', '{"en-US": "You currently show foundational leadership potential, possibly preferring independent work or execution roles over leadership positions.", "zh-CN": "您目前显示出基础领导潜力，可能更偏好独立工作或执行角色，而非领导角色。", "de-DE": "Sie zeigen derzeit grundlegendes Führungspotenzial und bevorzugen möglicherweise unabhängige Arbeit oder ausführende Rollen gegenüber Führungspositionen.", "fr-FR": "Vous démontrez actuellement un potentiel de leadership fondamental, préférant peut-être le travail indépendant ou les rôles d''exécution plutôt que les postes de direction.", "zh-TW": "您目前顯示出基礎領導潛力，可能更偏好獨立工作或執行角色，而非領導角色。"}', '{"en-US": "Leadership has many forms. You may currently be better suited for individual contribution or specialized fields. This doesn''t mean you can''t develop leadership abilities—it''s just not your current core strength. Important note: Leadership potential doesn''t equal personal value. Many organizations need excellent individual contributors. Choose development paths aligning with your values and strengths.", "zh-CN": "领导力有多种形式，您目前可能更适合专注于个人贡献或专业领域。这并不意味着您不能发展领导能力，只是当前不是您的核心优势。重要说明：领导潜力不等于价值。许多组织需要优秀的个人贡献者。选择与您的价值观和优势一致的发展路径。", "de-DE": "Führung hat viele Formen. Sie sind derzeit möglicherweise besser für individuelle Beiträge oder spezialisierte Bereiche geeignet. Das bedeutet nicht, dass Sie keine Führungsfähigkeiten entwickeln können – es ist einfach nicht Ihre aktuelle Kernstärke. Wichtiger Hinweis: Führungspotenzial ist nicht gleich persönlicher Wert. Viele Organisationen benötigen ausgezeichnete individuelle Beiträger. Wählen Sie Entwicklungswege, die mit Ihren Werten und Stärken übereinstimmen.", "fr-FR": "Le leadership prend de nombreuses formes. Vous êtes peut-être actuellement mieux adapté à une contribution individuelle ou à des domaines spécialisés. Cela ne signifie pas que vous ne pouvez pas développer vos capacités de leadership—ce n''est simplement pas votre force principale actuelle. Note importante : Le potentiel de leadership ne définit pas la valeur personnelle. De nombreuses organisations ont besoin d''excellents contributeurs individuels. Choisissez des voies de développement en accord avec vos valeurs et vos forces.", "zh-TW": "領導力有多種形式，您目前可能更適合專注於個人貢獻或專業領域。這並不意味著您不能發展領導能力，只是當前不是您的核心優勢。重要說明：領導潛力不等於價值。許多組織需要優秀的個人貢獻者。選擇與您的價值觀和優勢一致的發展路徑。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('25a6ebe8-968c-47dc-b524-a1214bdaab76', 'multiple_intelligences', '4', '69', '84', '#66BB6A', NOW(), NOW(), '{"zh-CN": {"人际智能": 78, "内省智能": 75, "空间智能": 82, "语言智能": 80, "音乐智能": 72, "自然观察智能": 74, "身体动觉智能": 80, "逻辑数学智能": 78}, "en-US": {"Musical": 72, "Spatial": 82, "Linguistic": 80, "Naturalistic": 74, "Interpersonal": 78, "Intrapersonal": 75, "Bodily-kinesthetic": 80, "Logical-mathematical": 78}, "de-DE": {"Musikalisch": 72, "Räumlich": 82, "Sprachlich": 80, "Naturalistisch": 74, "Zwischenmenschlich": 78, "Intrapersonal": 75, "Körperlich-kinästhetisch": 80, "Logisch-mathematisch": 78}, "fr-FR": {"Musical": 72, "Spatial": 82, "Linguistique": 80, "Naturaliste": 74, "Interpersonnel": 78, "Intrapersonnel": 75, "Corporel-kinesthésique": 80, "Logique-mathématique": 78}, "zh-TW": {"音樂智能": 72, "空間智能": 82, "語言智能": 80, "自然觀察智能": 74, "人際智能": 78, "內省智能": 75, "身體動覺智能": 80, "邏輯數學智能": 78}}', '{"zh-CN": "优势发展状态", "en-US": "Strength Development State", "de-DE": "Stärkenentwicklungszustand", "fr-FR": "État de Développement des Forces", "zh-TW": "優勢發展狀態"}', '{"zh-CN": "您在多个智能领域表现良好，并有1-3个明显的优势领域，智能组合既有广度又有一定深度。", "en-US": "You perform well in multiple intelligence domains with 1-3 clear strengths, having both breadth and some depth in intelligence profile.", "de-DE": "Sie schneiden in mehreren Intelligenzbereichen gut ab und haben 1-3 klare Stärken, mit sowohl Breite als auch einer gewissen Tiefe im Intelligenzprofil.", "fr-FR": "Vous excellez dans plusieurs domaines d''intelligence avec 1 à 3 forces claires, ayant à la fois de la largeur et une certaine profondeur dans votre profil d''intelligence.", "zh-TW": "您在多個智能領域表現良好，並有1-3個明顯的優勢領域，智能組合既有廣度又有一定深度。"}', '{"zh-CN": "您已经发展出明显的智能优势，同时在其他领域也有良好基础。这种组合使您既有专业深度又有适应广度。重要说明：优势智能若过度使用可能忽视其他重要智能的发展。保持智能发展的平衡性和整合性。", "en-US": "You''ve developed clear intelligence strengths while maintaining good foundation in other areas. This combination gives you both professional depth and adaptive breadth. Important note: Overusing strength intelligences may neglect other important intelligences. Maintain balance and integration in intelligence development.", "de-DE": "Sie haben klare Intelligenzstärken entwickelt, während Sie in anderen Bereichen eine gute Grundlage beibehalten. Diese Kombination gibt Ihnen sowohl professionelle Tiefe als auch adaptive Breite. Wichtiger Hinweis: Übermäßige Nutzung von Stärkenintelligenzen kann andere wichtige Intelligenzen vernachlässigen. Halten Sie ein Gleichgewicht und eine Integration in der Intelligenzentwicklung aufrecht.", "fr-FR": "Vous avez développé des forces d''intelligence claires tout en maintenant une bonne base dans d''autres domaines. Cette combinaison vous offre à la fois une profondeur professionnelle et une largeur adaptative. Note importante : Une utilisation excessive des intelligences fortes peut négliger le développement d''autres intelligences importantes. Maintenez un équilibre et une intégration dans le développement de vos intelligences.", "zh-TW": "您已經發展出明顯的智能優勢，同時在其他領域也有良好基礎。這種組合使您既有專業深度又有適應廣度。重要說明：優勢智能若過度使用可能忽視其他重要智能的發展。保持智能發展的平衡性和整合性。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('2710682a-5acf-42df-97d1-46d5fc68aa61', 'psychological_resilience', '1', '20', '36', '#FF6B6B', NOW(), NOW(), '{"zh-CN": {"情绪调节": 45, "认知灵活性": 40, "社会支持运用": 50, "适应性问题解决": 42}, "en-US": {"Emotional regulation": 45, "Cognitive flexibility": 40, "Social support utilization": 50, "Problem-solving adaptability": 42}, "de-DE": {"Emotionale Regulierung": 45, "Kognitive Flexibilität": 40, "Nutzung sozialer Unterstützung": 50, "Anpassungsfähigkeit bei Problemlösungen": 42}, "fr-FR": {"Régulation émotionnelle": 45, "Flexibilité cognitive": 40, "Utilisation du soutien social": 50, "Adaptabilité à la résolution de problèmes": 42}, "zh-TW": {"情緒調節": 45, "認知靈活性": 40, "社會支持運用": 50, "適應性問題解決": 42}}', '{"zh-CN": "较低心理韧性", "en-US": "Lower Resilience", "de-DE": "Niedrigere Resilienz", "fr-FR": "Résilience faible", "zh-TW": "較低心理韌性"}', '{"zh-CN": "您的心理韧性目前处于较低水平，面对挑战和挫折时恢复较慢，可能需要更多外部支持。", "en-US": "Your psychological resilience is currently at lower level, with slower recovery from challenges and setbacks, possibly needing more external support.", "de-DE": "Ihre psychische Widerstandsfähigkeit ist derzeit auf einem niedrigen Niveau, mit langsamerer Erholung von Herausforderungen und Rückschlägen, möglicherweise benötigen Sie mehr externe Unterstützung.", "fr-FR": "Votre résilience psychologique est actuellement à un niveau faible, avec une récupération plus lente face aux défis et aux revers, nécessitant peut-être plus de soutien externe.", "zh-TW": "您的心理韌性目前處於較低水平，面對挑戰和挫折時恢復較慢，可能需要更多外部支持。"}', '{"zh-CN": "面对生活中的挑战，您可能需要更多时间和支持来恢复。这并不意味着您脆弱，而是表明您需要更有意识地培养韧性技能。重要说明：心理韧性是可以通过练习发展的技能，不是固定特质。避免将自己标签为''脆弱的人''。", "en-US": "Facing life challenges, you may need more time and support to recover. This doesn''t mean you''re fragile but suggests needing more conscious resilience skill development. Important note: Psychological resilience is a developable skill, not fixed trait. Avoid labeling yourself as ''fragile person.''", "de-DE": "Angesichts der Herausforderungen des Lebens benötigen Sie möglicherweise mehr Zeit und Unterstützung, um sich zu erholen. Dies bedeutet nicht, dass Sie zerbrechlich sind, sondern dass Sie bewusster Resilienzfähigkeiten entwickeln müssen. Wichtiger Hinweis: Psychische Widerstandsfähigkeit ist eine entwickelbare Fähigkeit, keine feste Eigenschaft. Vermeiden Sie es, sich selbst als ''zerbrechliche Person'' zu bezeichnen.", "fr-FR": "Face aux défis de la vie, vous pourriez avoir besoin de plus de temps et de soutien pour vous rétablir. Cela ne signifie pas que vous êtes fragile, mais suggère que vous devez développer consciemment vos compétences en résilience. Note importante : La résilience psychologique est une compétence développable, pas une caractéristique fixe. Évitez de vous étiqueter comme une ''personne fragile''.", "zh-TW": "面對生活中的挑戰，您可能需要更多時間和支持來恢復。這並不意味著您脆弱，而是表明您需要更有意識地培養韌性技能。重要說明：心理韌性是可以通過練習發展的技能，不是固定特質。避免將自己標籤為''脆弱的人''。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('27af90d9-1e66-4b21-aafc-1d010a2562e7', 'stress_index', '4', '69', '84', '#FF6B6B', NOW(), NOW(), '{"zh-CN": {"工作压力": 80, "情绪压力": 78, "生活压力": 75, "身体压力反应": 72}, "en-US": {"Life stress": 75, "Work stress": 80, "Emotional stress": 78, "Physical stress response": 72}, "de-DE": {"Arbeitsstress": 80, "Emotionale Belastung": 78, "Lebensstress": 75, "Körperliche Stressreaktion": 72}, "fr-FR": {"Stress au travail": 80, "Stress émotionnel": 78, "Stress de la vie": 75, "Réponse physique au stress": 72}, "zh-TW": {"工作壓力": 80, "情緒壓力": 78, "生活壓力": 75, "身體壓力反應": 72}}', '{"zh-CN": "高压力水平", "en-US": "High Stress Level", "de-DE": "Hohes Stressniveau", "fr-FR": "Niveau de stress élevé", "zh-TW": "高壓力水平"}', '{"zh-CN": "您的压力水平较高，可能已经影响到您的身心健康、工作效率或人际关系。需要积极干预。", "en-US": "Your stress level is high, potentially affecting physical/mental health, work efficiency, or relationships. Active intervention needed.", "de-DE": "Ihr Stressniveau ist hoch und könnte Ihre körperliche/psychische Gesundheit, Arbeitseffizienz oder Beziehungen beeinträchtigen. Aktive Intervention erforderlich.", "fr-FR": "Votre niveau de stress est élevé, ce qui pourrait affecter votre santé physique/mentale, votre efficacité au travail ou vos relations. Une intervention active est nécessaire.", "zh-TW": "您的壓力水平較高，可能已經影響到您的身心健康、工作效率或人際關係。需要積極干預。"}', '{"zh-CN": "您目前正经历较高的压力水平，这可能已经对您的生活质量产生了负面影响。现在是采取行动的关键时刻。重要说明：高压力状态若持续超过3个月可能增加健康风险。如果出现焦虑、抑郁症状或身体不适，请寻求专业帮助。", "en-US": "You''re currently experiencing high stress levels that may be negatively affecting your quality of life. Now is crucial for taking action. Important note: High stress lasting over 3 months may increase health risks. If experiencing anxiety, depression symptoms, or physical discomfort, seek professional help.", "de-DE": "Sie erleben derzeit ein hohes Stressniveau, das sich negativ auf Ihre Lebensqualität auswirken könnte. Jetzt ist es entscheidend, Maßnahmen zu ergreifen. Wichtiger Hinweis: Anhaltender hoher Stress über 3 Monate kann Gesundheitsrisiken erhöhen. Wenn Sie Angst, Depressionssymptome oder körperliche Beschwerden verspüren, suchen Sie professionelle Hilfe.", "fr-FR": "Vous traversez actuellement un niveau de stress élevé qui pourrait affecter négativement votre qualité de vie. Il est crucial d''agir maintenant. Note importante : Un stress élevé durant plus de 3 mois peut augmenter les risques pour la santé. Si vous ressentez de l''anxiété, des symptômes de dépression ou un inconfort physique, consultez un professionnel.", "zh-TW": "您目前正經歷較高的壓力水平，這可能已經對您的生活質量產生了負面影響。現在是採取行動的關鍵時刻。重要說明：高壓力狀態若持續超過3個月可能增加健康風險。如果出現焦慮、抑鬱症狀或身體不適，請尋求專業幫助。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('38cd914b-8cff-4170-af45-b3f0bf5d7be1', 'stress_index', '2', '37', '52', '#42A5F5', NOW(), NOW(), '{"en-US": {"Life stress": 40, "Work stress": 45, "Emotional stress": 50, "Physical stress response": 42}, "zh-CN": {"工作压力": 45, "情绪压力": 50, "生活压力": 40, "身体压力反应": 42}, "de-DE": {"Lebensstress": 40, "Arbeitsstress": 45, "Emotionale Belastung": 50, "Körperliche Stressreaktion": 42}, "fr-FR": {"Stress de la vie": 40, "Stress au travail": 45, "Stress émotionnel": 50, "Réponse physique au stress": 42}, "zh-TW": {"生活壓力": 40, "工作壓力": 45, "情緒壓力": 50, "身體壓力反應": 42}}', '{"en-US": "Moderately Low Stress", "zh-CN": "中等偏下压力", "de-DE": "Mäßig niedriger Stress", "fr-FR": "Stress modérément bas", "zh-TW": "中等偏下壓力"}', '{"en-US": "Your stress level is moderately low. You can effectively manage stress most of the time but occasionally feel stress accumulation.", "zh-CN": "您的压力水平适中偏低，大多数情况下能有效管理压力，但偶尔会感到压力积累。", "de-DE": "Ihr Stresslevel ist mäßig niedrig. Sie können Stress die meiste Zeit effektiv bewältigen, spüren jedoch gelegentlich eine Stressansammlung.", "fr-FR": "Votre niveau de stress est modérément bas. Vous gérez efficacement le stress la plupart du temps, mais ressentez parfois une accumulation de stress.", "zh-TW": "您的壓力水平適中偏低，大多數情況下能有效管理壓力，但偶爾會感到壓力累積。"}', '{"en-US": "You generally handle daily stress well but may feel increased stress in specific situations or periods. Your stress coping mechanisms are basically effective. Important note: Note seasonal and life-cycle variations in stress levels. Reassess stress quarterly.", "zh-CN": "您总体上能够管理日常压力，但在某些特定情境或时期可能会感到压力增加。您的压力应对机制基本有效。重要说明：注意压力水平的季节性变化和生命周期变化。定期（每季度）重新评估压力水平。", "de-DE": "Sie bewältigen den täglichen Stress im Allgemeinen gut, könnten jedoch in bestimmten Situationen oder Zeiträumen erhöhten Stress verspüren. Ihre Stressbewältigungsmechanismen sind grundsätzlich effektiv. Wichtiger Hinweis: Beachten Sie saisonale und lebenszyklusbedingte Schwankungen des Stressniveaus. Bewerten Sie Ihren Stress vierteljährlich neu.", "fr-FR": "Vous gérez généralement bien le stress quotidien, mais vous pourriez ressentir un stress accru dans des situations ou périodes spécifiques. Vos mécanismes de gestion du stress sont globalement efficaces. Remarque importante : Prenez en compte les variations saisonnières et cycliques du stress. Réévaluez votre stress trimestriellement.", "zh-TW": "您總體上能夠管理日常壓力，但在某些特定情境或時期可能會感到壓力增加。您的壓力應對機制基本有效。重要說明：注意壓力水平的季節性變化和生命週期變化。定期（每季度）重新評估壓力水平。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('4498c3fa-ab4c-4003-9601-dc58abb2557b', 'life_satisfaction', '5', '85', '100', '#8A2BE2', NOW(), NOW(), '{"en-US": {"Meaning in life": 92, "Positive affect": 90, "Achievement & control": 94, "Relationship satisfaction": 88}, "zh-CN": {"关系满意度": 88, "情绪积极性": 90, "生活意义感": 92, "成就与掌控感": 94}, "de-DE": {"Lebenssinn": 92, "Positive Emotionen": 90, "Leistung & Kontrolle": 94, "Beziehungszufriedenheit": 88}, "fr-FR": {"Sens de la vie": 92, "Affects positifs": 90, "Réussite et contrôle": 94, "Satisfaction relationnelle": 88}, "zh-TW": {"關係滿意度": 88, "情緒積極性": 90, "生活意義感": 92, "成就與掌控感": 94}}', '{"en-US": "Very Satisfied", "zh-CN": "非常满意", "de-DE": "Sehr zufrieden", "fr-FR": "Très satisfait", "zh-TW": "非常滿意"}', '{"en-US": "You''re very satisfied with life, feeling fulfilled, meaningful, and happy, highly satisfied in important life domains.", "zh-CN": "您对生活非常满意，感到充实、有意义且快乐，在重要生活领域都感到高度满足。", "de-DE": "Sie sind sehr zufrieden mit Ihrem Leben, fühlen sich erfüllt, sinnvoll und glücklich und sind in wichtigen Lebensbereichen hochzufrieden.", "fr-FR": "Vous êtes très satisfait de votre vie, vous vous sentez épanoui, comblé et heureux, avec un haut niveau de satisfaction dans les domaines importants de votre vie.", "zh-TW": "您對生活非常滿意，感到充實、有意義且快樂，在重要生活領域都感到高度滿足。"}', '{"en-US": "You have extremely high life satisfaction, indicating your life highly aligns with values, goals, and relationships. This state is many people''s aspiration. Important note: Extreme satisfaction may make adapting to major life changes harder. Build psychological resilience for potential future challenges. Also, satisfaction naturally fluctuates over time—this is normal.", "zh-CN": "您对生活有极高的满意度，这表明您的生活与您的价值观、目标和关系高度契合。这种状态是许多人追求的目标。重要说明：极高的生活满意度在面临重大生活变化时可能更难适应。建立心理弹性以应对未来可能的挑战。同时注意，满意度可能随时间波动，这是正常的。", "de-DE": "Sie haben eine äußerst hohe Lebenszufriedenheit, was darauf hinweist, dass Ihr Leben stark mit Ihren Werten, Zielen und Beziehungen übereinstimmt. Dieser Zustand ist das Ziel vieler Menschen. Wichtiger Hinweis: Extreme Zufriedenheit kann es schwieriger machen, sich an größere Lebensveränderungen anzupassen. Bauen Sie psychologische Widerstandsfähigkeit auf, um potenziellen zukünftigen Herausforderungen zu begegnen. Außerdem schwankt die Zufriedenheit im Laufe der Zeit natürlich – das ist normal.", "fr-FR": "Vous avez une satisfaction de vie extrêmement élevée, ce qui indique que votre vie est en forte adéquation avec vos valeurs, objectifs et relations. Cet état est l''aspiration de nombreuses personnes. Remarque importante : Une satisfaction extrême peut rendre l''adaptation aux grands changements de vie plus difficile. Développez une résilience psychologique pour faire face aux défis futurs potentiels. De plus, la satisfaction fluctue naturellement avec le temps - c''est normal.", "zh-TW": "您對生活有極高的滿意度，這表明您的生活與您的價值觀、目標和關係高度契合。這種狀態是許多人追求的目標。重要說明：極高的生活滿意度在面臨重大生活變化時可能更難適應。建立心理韌性以應對未來可能的挑戰。同時注意，滿意度可能隨時間波動，這是正常的。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('45662a13-2b69-48c6-b1b2-67518a548594', 'life_satisfaction', '3', '53', '68', '#42A5F5', NOW(), NOW(), '{"en-US": {"Meaning in life": 65, "Positive affect": 60, "Achievement & control": 64, "Relationship satisfaction": 62}, "zh-CN": {"关系满意度": 62, "情绪积极性": 60, "生活意义感": 65, "成就与掌控感": 64}, "de-DE": {"Lebenssinn": 65, "Positive Emotionen": 60, "Leistung & Kontrolle": 64, "Beziehungszufriedenheit": 62}, "fr-FR": {"Sens de la vie": 65, "Affects positifs": 60, "Réussite et contrôle": 64, "Satisfaction relationnelle": 62}, "zh-TW": {"關係滿意度": 62, "情緒積極性": 60, "生活意義感": 65, "成就與掌控感": 64}}', '{"en-US": "Basically Satisfied", "zh-CN": "基本满意", "de-DE": "Grundsätzlich zufrieden", "fr-FR": "Globalement satisfait", "zh-TW": "基本滿意"}', '{"en-US": "You''re basically satisfied with life, with positive aspects and room for improvement—typical for most people.", "zh-CN": "您对生活基本满意，有积极的方面也有改进空间，这是大多数人的典型状态。", "de-DE": "Sie sind grundsätzlich mit Ihrem Leben zufrieden, mit positiven Aspekten und Raum für Verbesserungen – typisch für die meisten Menschen.", "fr-FR": "Vous êtes globalement satisfait de votre vie, avec des aspects positifs et des marges d''amélioration, ce qui est typique pour la plupart des gens.", "zh-TW": "您對生活基本滿意，有積極的方面也有改進空間，這是大多數人的典型狀態。"}', '{"en-US": "You''re generally satisfied with life while recognizing improvement areas. This balanced perspective is healthy and realistic. Important note: Basic satisfaction might lead to complacency, hindering growth. Maintain appropriate dissatisfaction to promote continuous development.", "zh-CN": "您对生活总体上感到满意，同时认识到有改进的空间。这种平衡的观点是健康且现实的。重要说明：基本满意状态可能使人自满，不再追求成长。注意保持适度的不满足感以促进持续发展。", "de-DE": "Sie sind im Allgemeinen mit Ihrem Leben zufrieden, erkennen jedoch Verbesserungsbereiche. Diese ausgewogene Perspektive ist gesund und realistisch. Wichtiger Hinweis: Grundlegende Zufriedenheit könnte zu Selbstzufriedenheit führen und das Wachstum behindern. Bewahren Sie eine angemessene Unzufriedenheit, um kontinuierliche Entwicklung zu fördern.", "fr-FR": "Vous êtes généralement satisfait de votre vie tout en reconnaissant des domaines d''amélioration. Cette perspective équilibrée est saine et réaliste. Remarque importante : Une satisfaction de base pourrait entraîner de la complaisance, freinant ainsi la croissance. Maintenez une insatisfaction appropriée pour favoriser un développement continu.", "zh-TW": "您對生活總體上感到滿意，同時認識到有改進的空間。這種平衡的觀點是健康且現實的。重要說明：基本滿意狀態可能使人自滿，不再追求成長。注意保持適度的不滿足感以促進持續發展。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('464708fa-63ab-483d-a59d-e927561f96bd', 'multiple_intelligences', '2', '37', '52', '#FFA726', NOW(), NOW(), '{"zh-CN": {"人际智能": 50, "内省智能": 47, "空间智能": 50, "语言智能": 50, "音乐智能": 45, "自然观察智能": 46, "身体动觉智能": 52, "逻辑数学智能": 48}, "en-US": {"Musical": 45, "Spatial": 50, "Linguistic": 50, "Naturalistic": 46, "Interpersonal": 50, "Intrapersonal": 47, "Bodily-kinesthetic": 52, "Logical-mathematical": 48}, "de-DE": {"Musikalisch": 45, "Räumlich": 50, "Sprachlich": 50, "Naturalistisch": 46, "Zwischenmenschlich": 50, "Intrapersönlich": 47, "Körperlich-kinästhetisch": 52, "Logisch-mathematisch": 48}, "fr-FR": {"Musical": 45, "Spatial": 50, "Linguistique": 50, "Naturaliste": 46, "Interpersonnel": 50, "Intrapersonnel": 47, "Corporel-kinesthésique": 52, "Logique-mathématique": 48}, "zh-TW": {"人際智能": 50, "內省智能": 47, "空間智能": 50, "語言智能": 50, "音樂智能": 45, "自然觀察智能": 46, "身體動覺智能": 52, "邏輯數學智能": 48}}', '{"zh-CN": "智能发展探索期", "en-US": "Exploration Development Phase", "de-DE": "Erkundungsentwicklungsphase", "fr-FR": "Phase de développement exploratoire", "zh-TW": "智能發展探索期"}', '{"zh-CN": "您在多个智能领域有所发展，可能已经识别出一两个相对优势，但整体仍处于探索和发展阶段。", "en-US": "You''ve developed multiple intelligences, possibly identifying one or two relative strengths, but overall still in exploration and development phase.", "de-DE": "Sie haben mehrere Intelligenzen entwickelt und möglicherweise ein oder zwei relative Stärken identifiziert, befinden sich jedoch insgesamt noch in der Erkundungs- und Entwicklungsphase.", "fr-FR": "Vous avez développé plusieurs intelligences, en identifiant peut-être une ou deux forces relatives, mais vous êtes globalement encore en phase d''exploration et de développement.", "zh-TW": "您在多個智能領域有所發展，可能已經識別出一兩個相對優勢，但整體仍處於探索和發展階段。"}', '{"zh-CN": "您正在发展多种智能，可能已经发现了自己的某些优势领域。继续探索和发展可以帮助您建立更丰富的智能组合。重要说明：智能发展不是比赛。重要的是找到与您兴趣、价值观和目标一致的智能组合，而不是追求全面高分。", "en-US": "You''re developing multiple intelligences, possibly already discovering some strengths. Continued exploration and development can help build richer intelligence profile. Important note: Intelligence development isn''t competition. What matters is finding intelligence profile aligning with your interests, values, and goals, not pursuing comprehensive high scores.", "de-DE": "Sie entwickeln mehrere Intelligenzen und haben möglicherweise bereits einige Stärken entdeckt. Fortgesetzte Erkundung und Entwicklung können helfen, ein reichhaltigeres Intelligenzprofil aufzubauen. Wichtiger Hinweis: Intelligenzentwicklung ist kein Wettbewerb. Wichtig ist, ein Intelligenzprofil zu finden, das mit Ihren Interessen, Werten und Zielen übereinstimmt, und nicht umfassende Höchstwerte zu verfolgen.", "fr-FR": "Vous développez plusieurs intelligences et avez peut-être déjà découvert certaines forces. Une exploration et un développement continus peuvent aider à construire un profil d''intelligence plus riche. Note importante : le développement de l''intelligence n''est pas une compétition. Ce qui compte, c''est de trouver un profil d''intelligence aligné avec vos intérêts, valeurs et objectifs, et non de viser des scores élevés partout.", "zh-TW": "您正在發展多種智能，可能已經發現了自己的某些優勢領域。繼續探索和發展可以幫助您建立更豐富的智能組合。重要說明：智能發展不是比賽。重要的是找到與您興趣、價值觀和目標一致的智能組合，而不是追求全面高分。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('4aa69ffa-c067-4111-8494-5c94006311cf', 'multiple_intelligences', '3', '53', '68', '#42A5F5', NOW(), NOW(), '{"en-US": {"Musical": 58, "Spatial": 64, "Linguistic": 65, "Naturalistic": 59, "Interpersonal": 63, "Intrapersonal": 60, "Bodily-kinesthetic": 66, "Logical-mathematical": 62}, "zh-CN": {"人际智能": 63, "内省智能": 60, "空间智能": 64, "语言智能": 65, "音乐智能": 58, "自然观察智能": 59, "身体动觉智能": 66, "逻辑数学智能": 62}, "de-DE": {"Musikalisch": 58, "Räumlich": 64, "Sprachlich": 65, "Naturalistisch": 59, "Zwischenmenschlich": 63, "Intrapersönlich": 60, "Körperlich-kinästhetisch": 66, "Logisch-mathematisch": 62}, "fr-FR": {"Musical": 58, "Spatial": 64, "Linguistique": 65, "Naturaliste": 59, "Interpersonnel": 63, "Intrapersonnel": 60, "Corporel-kinesthésique": 66, "Logique-mathématique": 62}, "zh-TW": {"人際智能": 63, "內省智能": 60, "空間智能": 64, "語言智能": 65, "音樂智能": 58, "自然觀察智能": 59, "身體動覺智能": 66, "邏輯數學智能": 62}}', '{"en-US": "Balanced Development State", "zh-CN": "平衡发展状态", "de-DE": "Ausgewogener Entwicklungszustand", "fr-FR": "État de développement équilibré", "zh-TW": "平衡發展狀態"}', '{"en-US": "Your multiple intelligences develop relatively balanced without extreme strengths/weaknesses, with moderate abilities across several domains.", "zh-CN": "您的多种智能发展相对平衡，没有极端强弱差异，可能在多个领域都有一定能力。", "de-DE": "Ihre multiplen Intelligenzen entwickeln sich relativ ausgewogen ohne extreme Stärken/Schwächen, mit moderaten Fähigkeiten in mehreren Bereichen.", "fr-FR": "Vos multiples intelligences se développent de manière relativement équilibrée, sans forces/faiblesses extrêmes, avec des capacités modérées dans plusieurs domaines.", "zh-TW": "您的多種智能發展相對平衡，沒有極端強弱差異，可能在多個領域都有一定能力。"}', '{"en-US": "Your intelligence development shows good balance, possibly enabling adaptation to various situations and learning styles. This balance is valuable learning and adaptation asset. Important note: Balanced development might sometimes lead to lacking distinct professional advantage. Consider developing 1-2 deep specialized areas based on balanced foundation.", "zh-CN": "您的智能发展呈现出良好的平衡性，这可能使您能够适应多种情境和学习方式。这种平衡是宝贵的学习和适应资产。重要说明：平衡发展有时可能导致缺乏突出的专业优势。考虑在平衡基础上发展1-2个深度专业领域。", "de-DE": "Ihre Intelligenzentwicklung zeigt eine gute Balance, die möglicherweise Anpassung an verschiedene Situationen und Lernstile ermöglicht. Diese Balance ist ein wertvolles Lern- und Anpassungsvermögen. Wichtiger Hinweis: Ausgewogene Entwicklung kann manchmal dazu führen, dass ein deutlicher beruflicher Vorteil fehlt. Erwägen Sie, 1-2 tief spezialisierte Bereiche auf der Grundlage einer ausgewogenen Grundlage zu entwickeln.", "fr-FR": "Le développement de vos intelligences montre un bon équilibre, permettant peut-être une adaptation à diverses situations et styles d''apprentissage. Cet équilibre est un atout précieux pour l''apprentissage et l''adaptation. Note importante : un développement équilibré peut parfois entraîner un manque d''avantage professionnel distinct. Envisagez de développer 1 à 2 domaines spécialisés en profondeur sur une base équilibrée.", "zh-TW": "您的智能發展呈現出良好的平衡性，這可能使您能夠適應多種情境和學習方式。這種平衡是寶貴的學習和適應資產。重要說明：平衡發展有時可能導致缺乏突出的專業優勢。考慮在平衡基礎上發展1-2個深度專業領域。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('55617a0a-fd06-4cc6-adec-ce66259b69c4', 'emotional_recognition', '5', '85', '100', '#8A2BE2', NOW(), NOW(), '{"en-US": {"Tone emotion perception": 90, "Body language interpretation": 85, "Facial expression recognition": 95, "Sensitivity to emotional changes": 92}, "zh-CN": {"肢体语言解读": 85, "语调情绪感知": 90, "面部表情识别": 95, "情绪变化敏感度": 92}, "de-DE": {"Tonemotionserkennung": 90, "Körperspracheinterpretation": 85, "Gesichtsausdruckserkennung": 95, "Empfindlichkeit gegenüber emotionalen Veränderungen": 92}, "fr-FR": {"Perception des émotions dans le ton": 90, "Interprétation du langage corporel": 85, "Reconnaissance des expressions faciales": 95, "Sensibilité aux changements émotionnels": 92}, "zh-TW": {"肢體語言解讀": 85, "語調情緒感知": 90, "面部表情識別": 95, "情緒變化敏感度": 92}}', '{"en-US": "Expert Level", "zh-CN": "专家水平", "de-DE": "Expertenniveau", "fr-FR": "Niveau expert", "zh-TW": "專家水平"}', '{"en-US": "You have excellent emotional recognition ability approaching professional level. You can sensitively detect subtle emotional signals and accurately interpret complex emotional states.", "zh-CN": "您具有出色的情绪识别能力，接近专业水平。能敏锐捕捉细微情绪信号，准确解读复杂情绪状态。", "de-DE": "Sie haben eine ausgezeichnete Fähigkeit zur emotionalen Erkennung, die sich einem professionellen Niveau nähert. Sie können subtile emotionale Signale sensibel wahrnehmen und komplexe emotionale Zustände genau interpretieren.", "fr-FR": "Vous avez une excellente capacité de reconnaissance émotionnelle proche du niveau professionnel. Vous pouvez détecter avec sensibilité des signaux émotionnels subtils et interpréter avec précision des états émotionnels complexes.", "zh-TW": "您具有出色的情緒識別能力，接近專業水平。能敏銳捕捉細微情緒信號，準確解讀複雜情緒狀態。"}', '{"en-US": "Your emotional recognition ability is outstanding, potentially giving you natural advantages in counseling, negotiation, or creative arts. This ability enables deep understanding of others. Important note: Extreme emotional sensitivity may lead to ''emotional overload,'' requiring regular emotional detox and self-protection practices. Avoid over-relying on emotional recognition while neglecting factual information.", "zh-CN": "您的情绪识别能力非常突出，可能在心理咨询、谈判、艺术创作等领域具有天然优势。这种能力使您能够深刻理解他人。重要说明：极高的情绪敏感度可能导致“情绪过载”，需要定期进行情绪排毒和自我保护练习。避免过度依赖情绪识别而忽视事实信息。", "de-DE": "Ihre Fähigkeit zur emotionalen Erkennung ist herausragend und könnte Ihnen natürliche Vorteile in der Beratung, Verhandlung oder in kreativen Künsten verschaffen. Diese Fähigkeit ermöglicht ein tiefes Verständnis für andere. Wichtiger Hinweis: Extreme emotionale Sensibilität kann zu ''emotionaler Überlastung'' führen, die regelmäßige emotionale Entgiftung und Selbstschutzpraktiken erfordert. Vermeiden Sie es, sich zu sehr auf die emotionale Erkennung zu verlassen und dabei sachliche Informationen zu vernachlässigen.", "fr-FR": "Votre capacité de reconnaissance émotionnelle est exceptionnelle, ce qui pourrait vous donner des avantages naturels dans le conseil, la négociation ou les arts créatifs. Cette capacité permet une compréhension profonde des autres. Note importante : Une sensibilité émotionnelle extrême peut entraîner une ''surcharge émotionnelle'', nécessitant une désintoxication émotionnelle régulière et des pratiques de protection personnelle. Évitez de trop vous fier à la reconnaissance émotionnelle au détriment des informations factuelles.", "zh-TW": "您的情緒識別能力非常突出，可能在心理諮詢、談判、藝術創作等領域具有天然優勢。這種能力使您能夠深刻理解他人。重要說明：極高的情緒敏感度可能導致“情緒過載”，需要定期進行情緒排毒和自我保護練習。避免過度依賴情緒識別而忽視事實信息。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('556f8658-2c21-456c-a5ee-6b9227e62f5b', 'multiple_intelligences', '5', '85', '100', '#8A2BE2', NOW(), NOW(), '{"en-US": {"Musical": 86, "Spatial": 94, "Linguistic": 92, "Naturalistic": 87, "Interpersonal": 92, "Intrapersonal": 88, "Bodily-kinesthetic": 90, "Logical-mathematical": 90}, "zh-CN": {"人际智能": 92, "内省智能": 88, "空间智能": 94, "语言智能": 92, "音乐智能": 86, "自然观察智能": 87, "身体动觉智能": 90, "逻辑数学智能": 90}, "de-DE": {"Musikalisch": 86, "Räumlich": 94, "Linguistisch": 92, "Naturalistisch": 87, "Interpersonal": 92, "Intrapersonal": 88, "Körperlich-kinästhetisch": 90, "Logisch-mathematisch": 90}, "fr-FR": {"Musical": 86, "Spatial": 94, "Linguistique": 92, "Naturaliste": 87, "Interpersonnel": 92, "Intrapersonnel": 88, "Corporel-kinesthésique": 90, "Logico-mathématique": 90}, "zh-TW": {"人際智能": 92, "內省智能": 88, "空間智能": 94, "語言智能": 92, "音樂智能": 86, "自然觀察智能": 87, "身體動覺智能": 90, "邏輯數學智能": 90}}', '{"en-US": "Highly Developed State", "zh-CN": "高度发展状态", "de-DE": "Hochentwickelter Zustand", "fr-FR": "État hautement développé", "zh-TW": "高度發展狀態"}', '{"en-US": "Your multiple intelligences are highly developed with several clear strengths, showing both depth and breadth in intelligence profile with comprehensive cognitive abilities.", "zh-CN": "您的多种智能高度发展，有多个明显优势领域，智能组合既有深度又有广度，显示出全面的认知能力。", "de-DE": "Ihre multiplen Intelligenzen sind hoch entwickelt mit mehreren klaren Stärken, was sowohl Tiefe als auch Breite im Intelligenzprofil mit umfassenden kognitiven Fähigkeiten zeigt.", "fr-FR": "Vos intelligences multiples sont hautement développées avec plusieurs points forts clairs, montrant à la fois profondeur et étendue dans votre profil d''intelligence avec des capacités cognitives complètes.", "zh-TW": "您的多種智能高度發展，有多個明顯優勢領域，智能組合既有深度又有廣度，顯示出全面的認知能力。"}', '{"en-US": "Your multiple intelligences are highly developed, indicating comprehensive and strong cognitive abilities and learning potential. This state results from long-term development and rich experiences. Important note: Highly developed intelligence profile may make you impatient with others'' learning difficulties. Adjust teaching and communication approaches to accommodate different intelligence development levels. Also, high development doesn''t mean mastery in all domains—accept being ''good enough'' in some areas.", "zh-CN": "您的多种智能都得到了高度发展，这表明您具有全面而强大的认知能力和学习潜力。这种状态是长期发展和丰富经历的结果。重要说明：高度发展的智能组合可能使您对他人的学习困难缺乏耐心。注意调整教学和沟通方式以适应不同的智能发展水平。同时，智能高度发展不代表所有领域都需精通，接受在某些领域保持“足够好”即可。", "de-DE": "Ihre multiplen Intelligenzen sind hoch entwickelt, was auf umfassende und starke kognitive Fähigkeiten und Lernpotenziale hinweist. Dieser Zustand ist das Ergebnis langfristiger Entwicklung und reicher Erfahrungen. Wichtiger Hinweis: Ein hochentwickeltes Intelligenzprofil kann dazu führen, dass Sie ungeduldig mit den Lernschwierigkeiten anderer werden. Passen Sie Ihre Lehr- und Kommunikationsansätze an unterschiedliche Intelligenzentwicklungsniveaus an. Außerdem bedeutet hohe Entwicklung nicht, dass Sie in allen Bereichen Meisterschaft erreichen müssen – akzeptieren Sie, in einigen Bereichen ''gut genug'' zu sein.", "fr-FR": "Vos intelligences multiples sont hautement développées, indiquant des capacités cognitives complètes et un fort potentiel d''apprentissage. Cet état résulte d''un développement à long terme et d''expériences riches. Note importante : Un profil d''intelligence hautement développé peut vous rendre impatient face aux difficultés d''apprentissage des autres. Adaptez vos approches d''enseignement et de communication pour tenir compte des différents niveaux de développement de l''intelligence. De plus, un développement élevé ne signifie pas une maîtrise dans tous les domaines – acceptez d''être ''suffisamment bon'' dans certains domaines.", "zh-TW": "您的多種智能都得到了高度發展，這表明您具有全面而強大的認知能力和學習潛力。這種狀態是長期發展和豐富經歷的結果。重要說明：高度發展的智能組合可能使您對他人的學習困難缺乏耐心。注意調整教學和溝通方式以適應不同的智能發展水平。同時，智能高度發展不代表所有領域都需精通，接受在某些領域保持“足夠好”即可。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('588e5152-6d9d-4497-b400-1f0a1b069f80', 'stress_index', '1', '20', '36', '#66BB6A', NOW(), NOW(), '{"en-US": {"Life stress": 25, "Work stress": 30, "Emotional stress": 20, "Physical stress response": 35}, "zh-CN": {"工作压力": 30, "情绪压力": 20, "生活压力": 25, "身体压力反应": 35}, "de-DE": {"Lebensstress": 25, "Arbeitsstress": 30, "Emotionale Belastung": 20, "Körperliche Stressreaktion": 35}, "fr-FR": {"Stress de la vie": 25, "Stress au travail": 30, "Stress émotionnel": 20, "Réponse physique au stress": 35}, "zh-TW": {"工作壓力": 30, "情緒壓力": 20, "生活壓力": 25, "身體壓力反應": 35}}', '{"en-US": "Low Stress Level", "zh-CN": "低压力水平", "de-DE": "Niedriges Stressniveau", "fr-FR": "Niveau de stress faible", "zh-TW": "低壓力水平"}', '{"en-US": "Your stress level is low, indicating relatively balanced current life state, adequate coping resources, or fewer stressors.", "zh-CN": "您的压力水平很低，表明您目前的生活状态相对平衡，应对压力资源充足，或压力源较少。", "de-DE": "Ihr Stressniveau ist niedrig, was auf einen relativ ausgeglichenen aktuellen Lebenszustand, ausreichende Bewältigungsressourcen oder weniger Stressfaktoren hinweist.", "fr-FR": "Votre niveau de stress est faible, indiquant un état de vie actuel relativement équilibré, des ressources d''adaptation adéquates ou moins de facteurs de stress.", "zh-TW": "您的壓力水平很低，表明您目前的生活狀態相對平衡，應對壓力資源充足，或壓力源較少。"}', '{"en-US": "Your current stress management is good with strong life balance. This may stem from effective coping strategies, good support systems, or fewer current life stressors. Important note: Long-term low stress might indicate insufficient challenge. Maintain appropriate growth-promoting stress for personal development. Avoid losing motivation due to excessively low stress.", "zh-CN": "您目前的压力管理状况良好，生活平衡感较强。这可能源于有效应对策略、支持系统良好或当前生活压力源较少。重要说明：低压力状态在长期来看可能意味着挑战不足，需注意保持适度的成长性压力以促进个人发展。避免因压力过低而失去动力。", "de-DE": "Ihr aktuelles Stressmanagement ist gut mit einer starken Lebensbalance. Dies könnte auf effektive Bewältigungsstrategien, gute Unterstützungssysteme oder weniger aktuelle Lebensstressoren zurückzuführen sein. Wichtiger Hinweis: Langfristig niedriger Stress könnte auf unzureichende Herausforderungen hinweisen. Halten Sie ein angemessenes wachstumsförderndes Stressniveau für die persönliche Entwicklung aufrecht. Vermeiden Sie es, aufgrund von zu niedrigem Stress die Motivation zu verlieren.", "fr-FR": "Votre gestion actuelle du stress est bonne avec un bon équilibre de vie. Cela peut provenir de stratégies d''adaptation efficaces, de bons systèmes de soutien ou de moins de facteurs de stress actuels. Note importante : Un stress faible à long terme pourrait indiquer un manque de défis. Maintenez un niveau de stress approprié favorisant la croissance pour le développement personnel. Évitez de perdre votre motivation en raison d''un stress excessivement faible.", "zh-TW": "您目前的壓力管理狀況良好，生活平衡感較強。這可能源於有效應對策略、支持系統良好或當前生活壓力源較少。重要說明：低壓力狀態在長期來看可能意味著挑戰不足，需注意保持適度的成長性壓力以促進個人發展。避免因壓力過低而失去動力。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('589d7de2-95ee-42e9-9175-3079162af60c', 'psychological_resilience', '3', '53', '68', '#42A5F5', NOW(), NOW(), '{"zh-CN": {"情绪调节": 70, "认知灵活性": 65, "社会支持运用": 62, "适应性问题解决": 68}, "en-US": {"Emotional regulation": 70, "Cognitive flexibility": 65, "Social support utilization": 62, "Problem-solving adaptability": 68}, "de-DE": {"Emotionale Regulierung": 70, "Kognitive Flexibilität": 65, "Nutzung sozialer Unterstützung": 62, "Anpassungsfähigkeit bei Problemlösungen": 68}, "fr-FR": {"Régulation émotionnelle": 70, "Flexibilité cognitive": 65, "Utilisation du soutien social": 62, "Adaptabilité à la résolution de problèmes": 68}, "zh-TW": {"情緒調節": 70, "認知靈活性": 65, "社會支持運用": 62, "適應性問題解決": 68}}', '{"zh-CN": "中等心理韧性", "en-US": "Moderate Resilience", "de-DE": "Mäßige Resilienz", "fr-FR": "Résilience modérée", "zh-TW": "中等心理韌性"}', '{"zh-CN": "您的心理韧性处于中等水平，与大多数人相似。能够应对日常挑战，但非常规或重大事件可能考验您的韧性。", "en-US": "Your psychological resilience is moderate, similar to most people. Can handle daily challenges but atypical or major events may test your resilience.", "de-DE": "Ihre psychologische Resilienz ist moderat, ähnlich wie bei den meisten Menschen. Sie können alltägliche Herausforderungen bewältigen, aber untypische oder größere Ereignisse könnten Ihre Resilienz auf die Probe stellen.", "fr-FR": "Votre résilience psychologique est modérée, similaire à celle de la plupart des gens. Vous pouvez gérer les défis quotidiens, mais des événements atypiques ou majeurs pourraient mettre votre résilience à l''épreuve.", "zh-TW": "您的心理韌性處於中等水平，與大多數人相似。能夠應對日常挑戰，但非常規或重大事件可能考驗您的韌性。"}', '{"zh-CN": "您具备基本的心理韧性，能够处理生活中的一般困难。继续加强这项能力将帮助您更好地应对未来的不确定性。重要说明：中等心理韧性在稳定环境中足够，但在快速变化或高度不确定的环境中可能需要加强。", "en-US": "You possess basic psychological resilience, able to handle general life difficulties. Further strengthening this ability will help better cope with future uncertainties. Important note: Moderate resilience may suffice in stable environments but need strengthening in rapidly changing or highly uncertain situations.", "de-DE": "Sie verfügen über grundlegende psychologische Resilienz und können allgemeine Lebensschwierigkeiten bewältigen. Eine weitere Stärkung dieser Fähigkeit wird Ihnen helfen, besser mit zukünftigen Unsicherheiten umzugehen. Wichtiger Hinweis: Eine moderate Resilienz mag in stabilen Umgebungen ausreichen, muss jedoch in sich schnell verändernden oder hochgradig unsicheren Situationen gestärkt werden.", "fr-FR": "Vous possédez une résilience psychologique de base, capable de gérer les difficultés générales de la vie. Renforcer davantage cette capacité vous aidera à mieux faire face aux incertitudes futures. Remarque importante : Une résilience modérée peut suffire dans des environnements stables, mais nécessite un renforcement dans des situations en rapide évolution ou hautement incertaines.", "zh-TW": "您具備基本的心理韌性，能夠處理生活中的一般困難。繼續加強這項能力將幫助您更好地應對未來的不確定性。重要說明：中等心理韌性在穩定環境中足夠，但在快速變化或高度不確定的環境中可能需要加強。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('5e4efcf6-d4b4-4163-b741-069f3b1de5d4', 'leadership_potential', '4', '69', '84', '#66BB6A', NOW(), NOW(), '{"zh-CN": {"团队协作": 78, "决策与担当": 76, "沟通影响力": 80, "目标与执行": 82}, "en-US": {"Goal & execution": 82, "Team collaboration": 78, "Decision & ownership": 76, "Communication & influence": 80}, "de-DE": {"Ziel & Ausführung": 82, "Teamzusammenarbeit": 78, "Entscheidungen & Verantwortung": 76, "Kommunikation & Einfluss": 80}, "fr-FR": {"Objectif & exécution": 82, "Collaboration en équipe": 78, "Décision & responsabilité": 76, "Communication & influence": 80}, "zh-TW": {"團隊協作": 78, "決策與擔當": 76, "溝通影響力": 80, "目標與執行": 82}}', '{"zh-CN": "高领导潜力", "en-US": "High Leadership Potential", "de-DE": "Hohes Führungspotenzial", "fr-FR": "Potentiel de leadership élevé", "zh-TW": "高領導潛力"}', '{"zh-CN": "您具有高领导潜力，展现出成为有效领导者的多方面素质，可能在多个情境中表现领导能力。", "en-US": "You have high leadership potential, showing multifaceted qualities of effective leader, possibly demonstrating leadership across multiple situations.", "de-DE": "Sie haben ein hohes Führungspotenzial und zeigen vielseitige Qualitäten eines effektiven Führers, möglicherweise in verschiedenen Situationen.", "fr-FR": "Vous avez un fort potentiel de leadership, montrant des qualités variées d''un leader efficace, pouvant démontrer du leadership dans plusieurs situations.", "zh-TW": "您具有高領導潛力，展現出成為有效領導者的多方面素質，可能在多個情境中表現領導能力。"}', '{"zh-CN": "您具有明显的领导潜力，可能已经在某些场合展现出领导能力。进一步发展和实践可以使您成为更自信、更有影响力的领导者。重要说明：高领导潜力可能使您过早承担过多责任。注意平衡领导职责与个人生活和专业发展。", "en-US": "You have noticeable leadership potential, possibly already showing leadership abilities in some contexts. Further development and practice can make you more confident and influential leader. Important note: High leadership potential might lead to prematurely assuming excessive responsibility. Balance leadership duties with personal life and professional development.", "de-DE": "Sie haben ein bemerkenswertes Führungspotenzial und zeigen möglicherweise bereits Führungsqualitäten in einigen Kontexten. Weitere Entwicklung und Übung können Sie zu einem selbstbewussteren und einflussreicheren Führer machen. Wichtiger Hinweis: Hohes Führungspotenzial könnte dazu führen, dass Sie vorzeitig zu viel Verantwortung übernehmen. Balancieren Sie Führungsaufgaben mit persönlichem Leben und beruflicher Entwicklung.", "fr-FR": "Vous avez un potentiel de leadership notable, montrant peut-être déjà des capacités de leadership dans certains contextes. Un développement et une pratique supplémentaires peuvent faire de vous un leader plus confiant et influent. Remarque importante : Un fort potentiel de leadership peut conduire à assumer prématurément des responsabilités excessives. Équilibrez les responsabilités de leadership avec votre vie personnelle et votre développement professionnel.", "zh-TW": "您具有明顯的領導潛力，可能已經在某些場合展現出領導能力。進一步發展和實踐可以使您成為更自信、更有影響力的領導者。重要說明：高領導潛力可能使您過早承擔過多責任。注意平衡領導職責與個人生活和專業發展。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('6b0ba1d3-cf70-4fd9-b5eb-f6840a92a612', 'leadership_potential', '5', '85', '100', '#8A2BE2', NOW(), NOW(), '{"en-US": {"Goal & execution": 94, "Team collaboration": 90, "Decision & ownership": 88, "Communication & influence": 92}, "zh-CN": {"团队协作": 90, "决策与担当": 88, "沟通影响力": 92, "目标与执行": 94}, "de-DE": {"Ziel & Ausführung": 94, "Teamzusammenarbeit": 90, "Entscheidungen & Verantwortung": 88, "Kommunikation & Einfluss": 92}, "fr-FR": {"Objectif & exécution": 94, "Collaboration en équipe": 90, "Décision & responsabilité": 88, "Communication & influence": 92}, "zh-TW": {"團隊協作": 90, "決策與擔當": 88, "溝通影響力": 92, "目標與執行": 94}}', '{"en-US": "Outstanding Leadership Potential", "zh-CN": "杰出领导潜力", "de-DE": "Herausragendes Führungspotenzial", "fr-FR": "Potentiel de leadership exceptionnel", "zh-TW": "傑出領導潛力"}', '{"en-US": "You have outstanding leadership potential, showing advanced leader''s multifaceted qualities, possibly naturally demonstrating leadership across various situations.", "zh-CN": "您具有杰出的领导潜力，展现出高级领导者的多方面素质，可能在多种情境中自然展现领导力。", "de-DE": "Sie haben außergewöhnliches Führungspotenzial und zeigen die facettenreichen Qualitäten eines fortgeschrittenen Führers. Möglicherweise demonstrieren Sie Führung auf natürliche Weise in verschiedenen Situationen.", "fr-FR": "Vous avez un potentiel de leadership exceptionnel, montrant les qualités variées d''un leader avancé, et pouvez démontrer naturellement du leadership dans diverses situations.", "zh-TW": "您具有傑出的領導潛力，展現出高級領導者的多方面素質，可能在多種情境中自然展現領導力。"}', '{"en-US": "Your leadership potential is exceptional, possibly showing leadership qualities surpassing peers or colleagues. This potential is valuable personal and organizational resource. Important note: Extreme leadership potential may come with excessive expectations and pressure. Manage others'' expectations while remaining authentic and humble. True leadership includes willingness to serve others and take responsibility.", "zh-CN": "您的领导潜力非常突出，可能已经展现出超越同龄人或同行的领导素质。这种潜力是宝贵的个人和组织资源。重要说明：极高的领导潜力可能伴随过高期望和压力。注意管理他人期望，同时保持真实和谦逊。真正的领导力包括服务他人和承担责任的意愿。", "de-DE": "Ihr Führungspotenzial ist außergewöhnlich und zeigt möglicherweise Führungsqualitäten, die Kollegen oder Gleichaltrige übertreffen. Dieses Potenzial ist eine wertvolle persönliche und organisatorische Ressource. Wichtiger Hinweis: Extremes Führungspotenzial kann mit übermäßigen Erwartungen und Druck einhergehen. Verwalten Sie die Erwartungen anderer, während Sie authentisch und bescheiden bleiben. Wahre Führung umfasst die Bereitschaft, anderen zu dienen und Verantwortung zu übernehmen.", "fr-FR": "Votre potentiel de leadership est exceptionnel, montrant des qualités de leader qui surpassent peut-être celles de vos pairs ou collègues. Ce potentiel est une ressource précieuse pour vous-même et votre organisation. Note importante : Un potentiel de leadership extrême peut entraîner des attentes et des pressions excessives. Gérez les attentes des autres tout en restant authentique et humble. Le véritable leadership inclut la volonté de servir les autres et d''assumer des responsabilités.", "zh-TW": "您的領導潛力非常突出，可能已經展現出超越同齡人或同行的領導素質。這種潛力是寶貴的個人和組織資源。重要說明：極高的領導潛力可能伴隨過高期望和壓力。注意管理他人期望，同時保持真實和謙遜。真正的領導力包括服務他人和承擔責任的意願。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('6debc4c5-80d8-43bd-bcc1-8a8fae7d272b', 'emotional_recognition', '4', '69', '84', '#66BB6A', NOW(), NOW(), '{"en-US": {"Tone emotion perception": 75, "Body language interpretation": 70, "Facial expression recognition": 85, "Sensitivity to emotional changes": 80}, "zh-CN": {"肢体语言解读": 70, "语调情绪感知": 75, "面部表情识别": 85, "情绪变化敏感度": 80}, "de-DE": {"Wahrnehmung von Tonemotionen": 75, "Interpretation von Körpersprache": 70, "Erkennung von Gesichtsausdrücken": 85, "Sensibilität für emotionale Veränderungen": 80}, "fr-FR": {"Perception des émotions dans le ton": 75, "Interprétation du langage corporel": 70, "Reconnaissance des expressions faciales": 85, "Sensibilité aux changements émotionnels": 80}, "zh-TW": {"語調情緒感知": 75, "肢體語言解讀": 70, "面部表情識別": 85, "情緒變化敏感度": 80}}', '{"en-US": "Proficient Level", "zh-CN": "熟练水平", "de-DE": "Fortgeschrittenes Niveau", "fr-FR": "Niveau compétent", "zh-TW": "熟練水平"}', '{"en-US": "You have good emotional recognition ability, accurately perceiving others'' emotional changes including subtle expressions and complex emotional combinations.", "zh-CN": "您具有良好的情绪识别能力，能准确感知他人的情绪变化，包括细微表情和复杂情绪组合。", "de-DE": "Sie haben eine gute Fähigkeit zur emotionalen Erkennung und können die emotionalen Veränderungen anderer, einschließlich subtiler Ausdrücke und komplexer emotionaler Kombinationen, genau wahrnehmen.", "fr-FR": "Vous avez une bonne capacité de reconnaissance émotionnelle, percevant avec précision les changements émotionnels des autres, y compris les expressions subtiles et les combinaisons émotionnelles complexes.", "zh-TW": "您具有良好的情緒識別能力，能準確感知他人的情緒變化，包括細微表情和複雜情緒組合。"}', '{"en-US": "You excel in emotional recognition, allowing you to generally respond appropriately in interpersonal relationships. This ability is particularly valuable in leadership, education, and service fields. Important note: High emotional recognition may make you more susceptible to others'' emotions. Pay attention to emotional boundaries and self-protection.", "zh-CN": "您在情绪识别方面表现出色，这使您在人际关系中通常能够做出恰当回应。这种能力在领导、教育、服务等领域尤其有价值。重要说明：高情绪识别能力可能使您更容易受到他人情绪影响，需注意情绪边界和自我保护。", "de-DE": "Sie zeichnen sich durch eine hervorragende emotionale Erkennung aus, die es Ihnen ermöglicht, in zwischenmenschlichen Beziehungen angemessen zu reagieren. Diese Fähigkeit ist besonders wertvoll in den Bereichen Führung, Bildung und Dienstleistung. Wichtiger Hinweis: Hohe emotionale Erkennung kann dazu führen, dass Sie anfälliger für die Emotionen anderer sind. Achten Sie auf emotionale Grenzen und Selbstschutz.", "fr-FR": "Vous excellez dans la reconnaissance émotionnelle, ce qui vous permet de répondre généralement de manière appropriée dans les relations interpersonnelles. Cette capacité est particulièrement précieuse dans les domaines du leadership, de l''éducation et des services. Note importante : Une reconnaissance émotionnelle élevée peut vous rendre plus sensible aux émotions des autres. Faites attention aux limites émotionnelles et à la protection de soi.", "zh-TW": "您在情緒識別方面表現出色，這使您在人際關係中通常能夠做出恰當回應。這種能力在領導、教育、服務等領域尤其有價值。重要說明：高情緒識別能力可能使您更容易受到他人情緒影響，需注意情緒邊界和自我保護。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('77b6d74a-ce29-4d48-b4b6-7d39b4a304bf', 'psychological_resilience', '4', '69', '84', '#66BB6A', NOW(), NOW(), '{"en-US": {"Emotional regulation": 85, "Cognitive flexibility": 78, "Social support utilization": 75, "Problem-solving adaptability": 82}, "zh-CN": {"情绪调节": 85, "认知灵活性": 78, "社会支持运用": 75, "适应性问题解决": 82}, "de-DE": {"Emotionale Regulierung": 85, "Kognitive Flexibilität": 78, "Nutzung sozialer Unterstützung": 75, "Anpassungsfähigkeit bei Problemlösungen": 82}, "fr-FR": {"Régulation émotionnelle": 85, "Flexibilité cognitive": 78, "Utilisation du soutien social": 75, "Adaptabilité à la résolution de problèmes": 82}, "zh-TW": {"情緒調節": 85, "認知靈活性": 78, "社會支持運用": 75, "適應性問題解決": 82}}', '{"en-US": "High Resilience", "zh-CN": "高心理韧性", "de-DE": "Hohe Resilienz", "fr-FR": "Résilience élevée", "zh-TW": "高心理韌性"}', '{"en-US": "You have high psychological resilience, effectively responding to challenges and setbacks, recovering and learning from them, with strong adaptability to change.", "zh-CN": "您具有较高的心理韧性，能够有效应对挑战和挫折，从中恢复并学习，适应变化的能力较强。", "de-DE": "Sie haben eine hohe psychologische Resilienz und reagieren effektiv auf Herausforderungen und Rückschläge, erholen sich und lernen daraus, mit einer starken Anpassungsfähigkeit an Veränderungen.", "fr-FR": "Vous avez une résilience psychologique élevée, répondant efficacement aux défis et aux revers, en vous rétablissant et en apprenant d''eux, avec une forte capacité d''adaptation au changement.", "zh-TW": "您具有較高的心理韌性，能夠有效應對挑戰和挫折，從中恢復並學習，適應變化的能力較強。"}', '{"en-US": "Your psychological resilience is significant strength, helping maintain functioning during difficult times and growing from experiences. This ability is particularly valuable in leadership, entrepreneurship, and change management. Important note: High resilience may sometimes lead to neglecting personal needs or underestimating challenge severity. Balance resilience with self-care.", "zh-CN": "您的心理韧性是您的重要优势，帮助您在困难时期保持功能并从经验中成长。这种能力在领导、创业和变革管理中尤其宝贵。重要说明：高心理韧性有时可能导致忽视自身需求或低估挑战的严重性。注意平衡韧性与自我关怀。", "de-DE": "Ihre psychologische Resilienz ist eine bedeutende Stärke, die Ihnen hilft, während schwieriger Zeiten funktionsfähig zu bleiben und aus Erfahrungen zu wachsen. Diese Fähigkeit ist besonders wertvoll in den Bereichen Führung, Unternehmertum und Veränderungsmanagement. Wichtiger Hinweis: Hohe Resilienz kann manchmal dazu führen, dass persönliche Bedürfnisse vernachlässigt oder die Schwere von Herausforderungen unterschätzt wird. Balancieren Sie Resilienz mit Selbstfürsorge.", "fr-FR": "Votre résilience psychologique est une force importante, vous aidant à maintenir vos fonctions pendant les périodes difficiles et à grandir grâce aux expériences. Cette capacité est particulièrement précieuse dans les domaines du leadership, de l''entrepreneuriat et de la gestion du changement. Note importante : Une résilience élevée peut parfois conduire à négliger ses besoins personnels ou à sous-estimer la gravité des défis. Équilibrez la résilience avec le soin de soi.", "zh-TW": "您的心理韌性是您的重要優勢，幫助您在困難時期保持功能並從經驗中成長。這種能力在領導、創業和變革管理中尤其寶貴。重要說明：高心理韌性有時可能導致忽視自身需求或低估挑戰的嚴重性。注意平衡韌性與自我關懷。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('831b5d29-4696-4136-b76e-5e616d40d7fa', 'stress_index', '3', '53', '68', '#FFA726', NOW(), NOW(), '{"zh-CN": {"工作压力": 65, "情绪压力": 60, "生活压力": 55, "身体压力反应": 50}, "en-US": {"Life stress": 55, "Work stress": 65, "Emotional stress": 60, "Physical stress response": 50}, "de-DE": {"Lebensstress": 55, "Arbeitsstress": 65, "Emotionaler Stress": 60, "Körperliche Stressreaktion": 50}, "fr-FR": {"Stress de la vie": 55, "Stress au travail": 65, "Stress émotionnel": 60, "Réponse physique au stress": 50}, "zh-TW": {"生活壓力": 55, "工作壓力": 65, "情緒壓力": 60, "身體壓力反應": 50}}', '{"zh-CN": "中等压力", "en-US": "Moderate Stress", "de-DE": "Moderater Stress", "fr-FR": "Stress modéré", "zh-TW": "中等壓力"}', '{"zh-CN": "您的压力处于中等水平，这是许多成年人的典型状态。您能应对日常压力，但可能偶尔感到不堪重负。", "en-US": "Your stress is at moderate level, typical for many adults. You can handle daily stress but may occasionally feel overwhelmed.", "de-DE": "Ihr Stress liegt auf einem moderaten Niveau, typisch für viele Erwachsene. Sie können mit täglichem Stress umgehen, fühlen sich aber möglicherweise gelegentlich überfordert.", "fr-FR": "Votre stress est à un niveau modéré, typique pour de nombreux adultes. Vous pouvez gérer le stress quotidien mais pouvez parfois vous sentir dépassé.", "zh-TW": "您的壓力處於中等水平，這是許多成年人的典型狀態。您能應對日常壓力，但可能偶爾感到不堪重負。"}', '{"zh-CN": "您正经历着现代生活中常见的压力水平。虽然大多时候能够应对，但压力可能已经开始影响您的某些生活领域。重要说明：中等压力若持续存在可能逐渐影响健康。注意压力相关的身体信号（如头痛、睡眠问题）。", "en-US": "You''re experiencing common modern-life stress levels. While usually manageable, stress may already be affecting some life areas. Important note: Persistent moderate stress may gradually affect health. Notice stress-related physical signals (headaches, sleep issues).", "de-DE": "Sie erleben die häufigen Stressniveaus des modernen Lebens. Obwohl normalerweise handhabbar, könnte der Stress bereits einige Lebensbereiche beeinträchtigen. Wichtiger Hinweis: Anhaltender moderater Stress kann allmählich die Gesundheit beeinträchtigen. Achten Sie auf stressbedingte körperliche Signale (Kopfschmerzen, Schlafprobleme).", "fr-FR": "Vous ressentez des niveaux de stress courants dans la vie moderne. Bien que généralement gérable, le stress peut déjà affecter certains domaines de votre vie. Note importante : un stress modéré persistant peut progressivement affecter la santé. Faites attention aux signaux physiques liés au stress (maux de tête, problèmes de sommeil).", "zh-TW": "您正經歷著現代生活中常見的壓力水平。雖然大多時候能夠應對，但壓力可能已經開始影響您的某些生活領域。重要說明：中等壓力若持續存在可能逐漸影響健康。注意壓力相關的身體信號（如頭痛、睡眠問題）。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('860980e5-0aa3-472f-963a-03c0d5c569e6', 'multiple_intelligences', '1', '20', '36', '#FF6B6B', NOW(), NOW(), '{"zh-CN": {"人际智能": 35, "内省智能": 35, "空间智能": 35, "语言智能": 35, "音乐智能": 35, "自然观察智能": 35, "身体动觉智能": 35, "逻辑数学智能": 35}, "en-US": {"Musical": 35, "Spatial": 35, "Linguistic": 35, "Naturalistic": 35, "Interpersonal": 35, "Intrapersonal": 35, "Bodily-kinesthetic": 35, "Logical-mathematical": 35}, "de-DE": {"Musikalisch": 35, "Räumlich": 35, "Linguistisch": 35, "Naturalistisch": 35, "Interpersonal": 35, "Intrapersonal": 35, "Körperlich-kinästhetisch": 35, "Logisch-mathematisch": 35}, "fr-FR": {"Musical": 35, "Spatial": 35, "Linguistique": 35, "Naturaliste": 35, "Interpersonnel": 35, "Intrapersonnel": 35, "Corporel-kinesthésique": 35, "Logique-mathématique": 35}, "zh-TW": {"人際智能": 35, "內省智能": 35, "空間智能": 35, "語言智能": 35, "音樂智能": 35, "自然觀察智能": 35, "身體動覺智能": 35, "邏輯數學智能": 35}}', '{"zh-CN": "智能发展基础期", "en-US": "Foundational Development Phase", "de-DE": "Grundlegende Entwicklungsphase", "fr-FR": "Phase de développement fondamental", "zh-TW": "智能發展基礎期"}', '{"zh-CN": "您在多种智能领域的发展处于基础阶段，可能有一两个相对优势领域，但整体发展较为平均或偏低。", "en-US": "Your multiple intelligence development is at foundational stage, possibly with one or two relative strengths but overall development is relatively average or low.", "de-DE": "Ihre Entwicklung in verschiedenen Intelligenzbereichen befindet sich auf einer grundlegenden Stufe, möglicherweise mit ein oder zwei relativen Stärken, aber insgesamt ist die Entwicklung relativ durchschnittlich oder niedrig.", "fr-FR": "Votre développement dans divers domaines d''intelligence est à un stade fondamental, avec peut-être une ou deux forces relatives, mais un développement globalement moyen ou faible.", "zh-TW": "您在多種智能領域的發展處於基礎階段，可能有一兩個相對優勢領域，但整體發展較為平均或偏低。"}', '{"zh-CN": "每个人的智能发展轨迹不同。您目前可能还在探索不同智能领域，尚未发现或发展自己的优势智能。这是完全正常的探索过程。重要说明：智能测试分数不代表固定能力。所有智能都可以通过练习和发展得到提升。避免将低分解释为“没有天赋”。", "en-US": "Everyone''s intelligence development trajectory differs. You may still be exploring different intelligence domains, not yet discovering or developing your strengths. This is completely normal exploration process. Important note: Intelligence test scores don''t represent fixed abilities. All intelligences can improve through practice and development. Avoid interpreting low scores as ''lack of talent.''", "de-DE": "Die Entwicklungsverläufe der Intelligenz sind bei jedem Menschen unterschiedlich. Sie könnten noch dabei sein, verschiedene Intelligenzbereiche zu erkunden, ohne Ihre Stärken entdeckt oder entwickelt zu haben. Dies ist ein völlig normaler Erkundungsprozess. Wichtiger Hinweis: Intelligenztestergebnisse stellen keine festen Fähigkeiten dar. Alle Intelligenzen können durch Übung und Entwicklung verbessert werden. Vermeiden Sie es, niedrige Punktzahlen als „fehlendes Talent“ zu interpretieren.", "fr-FR": "Le développement de l''intelligence suit des trajectoires différentes pour chacun. Vous explorez peut-être encore différents domaines d''intelligence, sans avoir encore découvert ou développé vos forces. C''est un processus d''exploration tout à fait normal. Note importante : les scores des tests d''intelligence ne représentent pas des capacités fixes. Toutes les intelligences peuvent être améliorées par la pratique et le développement. Évitez d''interpréter des scores faibles comme un ''manque de talent''.", "zh-TW": "每個人的智能發展軌跡不同。您目前可能還在探索不同智能領域，尚未發現或發展自己的優勢智能。這是完全正常的探索過程。重要說明：智能測試分數不代表固定能力。所有智能都可以通過練習和發展得到提升。避免將低分解釋為「沒有天賦」。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('9652cebe-13ad-4a53-be3a-7255b0937239', 'psychological_resilience', '2', '37', '52', '#FFA726', NOW(), NOW(), '{"en-US": {"Emotional regulation": 60, "Cognitive flexibility": 55, "Social support utilization": 58, "Problem-solving adaptability": 57}, "zh-CN": {"情绪调节": 60, "认知灵活性": 55, "社会支持运用": 58, "适应性问题解决": 57}, "de-DE": {"Emotionale Regulierung": 60, "Kognitive Flexibilität": 55, "Nutzung sozialer Unterstützung": 58, "Anpassungsfähigkeit bei Problemlösungen": 57}, "fr-FR": {"Régulation émotionnelle": 60, "Flexibilité cognitive": 55, "Utilisation du soutien social": 58, "Adaptabilité à la résolution de problèmes": 57}, "zh-TW": {"情緒調節": 60, "認知靈活性": 55, "社會支持運用": 58, "適應性問題解決": 57}}', '{"en-US": "Developing Resilience", "zh-CN": "发展中的心理韧性", "de-DE": "Entwicklung von Resilienz", "fr-FR": "Développement de la résilience", "zh-TW": "發展中的心理韌性"}', '{"en-US": "Your psychological resilience is developing, with some recovery capacity but may struggle with major challenges or consecutive setbacks.", "zh-CN": "您的心理韧性正在发展中，有一定的恢复能力，但在重大挑战或连续挫折面前可能感到吃力。", "de-DE": "Ihre psychologische Widerstandsfähigkeit entwickelt sich, mit einer gewissen Erholungsfähigkeit, aber Sie könnten bei größeren Herausforderungen oder aufeinanderfolgenden Rückschlägen Schwierigkeiten haben.", "fr-FR": "Votre résilience psychologique est en développement, avec une certaine capacité de récupération, mais vous pourriez avoir du mal à faire face à des défis majeurs ou à des revers consécutifs.", "zh-TW": "您的心理韌性正在發展中，有一定的恢復能力，但在重大挑戰或連續挫折面前可能感到吃力。"}', '{"en-US": "You''ve begun developing psychological resilience but may still find larger difficulties challenging. Continuing this skill development will help better handle life''s ups and downs. Important note: Resilience development isn''t linear—expect fluctuations. Be patient, celebrate small progress.", "zh-CN": "您已经开始发展心理韧性，但在面对较大困难时可能仍会感到挑战。继续发展这项技能将帮助您更好地应对生活中的起伏。重要说明：心理韧性的发展不是线性的，可能会有起伏。对自己保持耐心，庆祝小的进步。", "de-DE": "Sie haben begonnen, Ihre psychologische Widerstandsfähigkeit zu entwickeln, könnten jedoch bei größeren Schwierigkeiten weiterhin Herausforderungen erleben. Die Weiterentwicklung dieser Fähigkeit wird Ihnen helfen, die Höhen und Tiefen des Lebens besser zu bewältigen. Wichtiger Hinweis: Die Entwicklung von Widerstandsfähigkeit ist nicht linear – Schwankungen sind zu erwarten. Seien Sie geduldig und feiern Sie kleine Fortschritte.", "fr-FR": "Vous avez commencé à développer votre résilience psychologique, mais vous pourriez encore trouver les grandes difficultés exigeantes. Continuer à développer cette compétence vous aidera à mieux gérer les hauts et les bas de la vie. Note importante : Le développement de la résilience n''est pas linéaire - attendez-vous à des fluctuations. Soyez patient et célébrez les petits progrès.", "zh-TW": "您已經開始發展心理韌性，但在面對較大困難時可能仍會感到挑戰。繼續發展這項技能將幫助您更好地應對生活中的起伏。重要說明：心理韌性的發展不是線性的，可能會有起伏。對自己保持耐心，慶祝小的進步。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('9721dab0-34b4-4b6e-b237-e70440cf0246', 'emotional_recognition', '3', '53', '68', '#42A5F5', NOW(), NOW(), '{"zh-CN": {"肢体语言解读": 60, "语调情绪感知": 65, "面部表情识别": 70, "情绪变化敏感度": 75}, "en-US": {"Tone emotion perception": 65, "Body language interpretation": 60, "Facial expression recognition": 70, "Sensitivity to emotional changes": 75}, "de-DE": {"Tonwahrnehmung von Emotionen": 65, "Interpretation der Körpersprache": 60, "Erkennung von Gesichtsausdrücken": 70, "Sensibilität für emotionale Veränderungen": 75}, "fr-FR": {"Perception des émotions dans le ton": 65, "Interprétation du langage corporel": 60, "Reconnaissance des expressions faciales": 70, "Sensibilité aux changements émotionnels": 75}, "zh-TW": {"語調情緒感知": 65, "肢體語言解讀": 60, "面部表情識別": 70, "情緒變化敏感度": 75}}', '{"zh-CN": "平均水平", "en-US": "Average Level", "de-DE": "Durchschnittliches Niveau", "fr-FR": "Niveau moyen", "zh-TW": "平均水平"}', '{"zh-CN": "您具有中等水平的情绪识别能力，与大多数人相似。能准确识别常见情绪，但在压力下或面对复杂情绪时可能出错。", "en-US": "You have medium-level emotional recognition ability, similar to most people. You can accurately identify common emotions but may make errors under stress or with complex emotions.", "de-DE": "Sie haben eine mittlere Fähigkeit zur Emotionserkennung, ähnlich wie die meisten Menschen. Sie können gängige Emotionen genau identifizieren, könnten jedoch unter Stress oder bei komplexen Emotionen Fehler machen.", "fr-FR": "Vous avez une capacité de reconnaissance émotionnelle de niveau moyen, similaire à la plupart des gens. Vous pouvez identifier avec précision les émotions courantes, mais vous pourriez faire des erreurs sous stress ou face à des émotions complexes.", "zh-TW": "您具有中等水平的情緒識別能力，與大多數人相似。能準確識別常見情緒，但在壓力下或面對複雜情緒時可能出錯。"}', '{"zh-CN": "您的情绪识别能力处于大众平均水平，能满足大多数日常社交需求。继续提升将有助于建立更深层的人际关系。重要说明：情绪识别不是读心术，总有不确定性。接受一定程度的误判是正常的。", "en-US": "Your emotional recognition ability is at the general population average, sufficient for most daily social interactions. Further improvement will help build deeper relationships. Important note: Emotional recognition isn''t mind-reading; there''s always uncertainty. Accepting some degree of misinterpretation is normal.", "de-DE": "Ihre Fähigkeit zur Emotionserkennung liegt im Durchschnitt der allgemeinen Bevölkerung und reicht für die meisten täglichen sozialen Interaktionen aus. Eine weitere Verbesserung wird helfen, tiefere Beziehungen aufzubauen. Wichtiger Hinweis: Emotionserkennung ist keine Gedankenleserei; es gibt immer Unsicherheiten. Es ist normal, ein gewisses Maß an Fehlinterpretation zu akzeptieren.", "fr-FR": "Votre capacité de reconnaissance émotionnelle est dans la moyenne de la population générale, suffisante pour la plupart des interactions sociales quotidiennes. Une amélioration supplémentaire aidera à établir des relations plus profondes. Note importante : La reconnaissance émotionnelle n''est pas de la lecture des pensées ; il y a toujours une part d''incertitude. Accepter un certain degré de mauvaise interprétation est normal.", "zh-TW": "您的情緒識別能力處於大眾平均水平，能滿足大多數日常社交需求。繼續提升將有助於建立更深層的人際關係。重要說明：情緒識別不是讀心術，總有不確定性。接受一定程度的誤判是正常的。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('97cb7338-39ca-468a-ab4e-685a2d84a721', 'life_satisfaction', '2', '37', '52', '#FFA726', NOW(), NOW(), '{"en-US": {"Meaning in life": 50, "Positive affect": 45, "Achievement & control": 48, "Relationship satisfaction": 52}, "zh-CN": {"关系满意度": 52, "情绪积极性": 45, "生活意义感": 50, "成就与掌控感": 48}, "de-DE": {"Beziehungszufriedenheit": 52, "Positive Emotionen": 45, "Lebenssinn": 50, "Leistung und Kontrolle": 48}, "fr-FR": {"Satisfaction relationnelle": 52, "Affects positifs": 45, "Sens de la vie": 50, "Accomplissement et contrôle": 48}, "zh-TW": {"關係滿意度": 52, "情緒積極性": 45, "生活意義感": 50, "成就與掌控感": 48}}', '{"en-US": "Somewhat Dissatisfied", "zh-CN": "有些不满意", "de-DE": "Etwas unzufrieden", "fr-FR": "Plutôt insatisfait", "zh-TW": "有些不滿意"}', '{"en-US": "You have some life dissatisfaction, possibly dissatisfied with specific areas while other aspects remain acceptable.", "zh-CN": "您对生活有一定程度的不满，可能在某些特定领域感到不满意，但其他方面还能接受。", "de-DE": "Sie haben eine gewisse Unzufriedenheit mit Ihrem Leben, möglicherweise in bestimmten Bereichen, während andere Aspekte akzeptabel bleiben.", "fr-FR": "Vous avez une certaine insatisfaction vis-à-vis de votre vie, peut-être insatisfait dans certains domaines tandis que d''autres aspects restent acceptables.", "zh-TW": "您對生活有一定程度的不滿，可能在某些特定領域感到不滿意，但其他方面還能接受。"}', '{"en-US": "You have mixed feelings about life—satisfied with some aspects, not others. Clarifying these differences enables targeted improvement. Important note: Life satisfaction is subjective, influenced by multiple factors. Avoid excessive comparison with others; focus on personal progress rather than absolute state.", "zh-CN": "您对生活有混合的感受，某些方面令您满意，而另一些方面则不然。明确这些差异可以帮助您有针对性地改进。重要说明：生活满意度是主观感受，受多种因素影响。避免与他人过度比较，关注自己的进步而非绝对状态。", "de-DE": "Sie haben gemischte Gefühle über Ihr Leben – mit einigen Aspekten sind Sie zufrieden, mit anderen nicht. Das Klären dieser Unterschiede ermöglicht gezielte Verbesserungen. Wichtiger Hinweis: Lebenszufriedenheit ist subjektiv und wird von mehreren Faktoren beeinflusst. Vermeiden Sie übermäßige Vergleiche mit anderen; konzentrieren Sie sich auf Ihren persönlichen Fortschritt statt auf einen absoluten Zustand.", "fr-FR": "Vous avez des sentiments mitigés à propos de la vie - satisfait de certains aspects, pas d''autres. Clarifier ces différences permet des améliorations ciblées. Note importante : La satisfaction de vie est subjective, influencée par de multiples facteurs. Évitez les comparaisons excessives avec les autres ; concentrez-vous sur vos progrès personnels plutôt que sur un état absolu.", "zh-TW": "您對生活有混合的感受，某些方面令您滿意，而另一些方面則不然。明確這些差異可以幫助您有針對性地改進。重要說明：生活滿意度是主觀感受，受多種因素影響。避免與他人過度比較，關注自己的進步而非絕對狀態。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('97ccdc06-b43d-4760-8023-2b2d21225d41', 'stress_index', '5', '85', '100', '#D32F2F', NOW(), NOW(), '{"en-US": {"Life stress": 90, "Work stress": 95, "Emotional stress": 92, "Physical stress response": 88}, "zh-CN": {"工作压力": 95, "情绪压力": 92, "生活压力": 90, "身体压力反应": 88}, "de-DE": {"Lebensstress": 90, "Arbeitsstress": 95, "Emotionale Belastung": 92, "Physische Stressreaktion": 88}, "fr-FR": {"Stress de la vie": 90, "Stress au travail": 95, "Stress émotionnel": 92, "Réponse physique au stress": 88}, "zh-TW": {"生活壓力": 90, "工作壓力": 95, "情緒壓力": 92, "身體壓力反應": 88}}', '{"en-US": "Very High Stress", "zh-CN": "极高压力", "de-DE": "Sehr hoher Stress", "fr-FR": "Stress très élevé", "zh-TW": "極高壓力"}', '{"en-US": "Your stress level is very high, likely significantly affecting physical/mental health. Strongly recommend immediate action and professional support.", "zh-CN": "您的压力水平非常高，可能已经对身心健康造成显著影响。强烈建议立即采取措施并考虑专业支持。", "de-DE": "Ihr Stresslevel ist sehr hoch und wirkt sich wahrscheinlich erheblich auf Ihre körperliche und geistige Gesundheit aus. Es wird dringend empfohlen, sofort Maßnahmen zu ergreifen und professionelle Unterstützung in Anspruch zu nehmen.", "fr-FR": "Votre niveau de stress est très élevé, ce qui affecte probablement de manière significative votre santé physique et mentale. Il est fortement recommandé de prendre des mesures immédiates et de demander un soutien professionnel.", "zh-TW": "您的壓力水平非常高，可能已經對身心健康造成顯著影響。強烈建議立即採取措施並考慮專業支持。"}', '{"en-US": "You''re in a very high stress state that may seriously impact health, judgment, and quality of life. Immediate attention to stress management is essential. Important note: Very high stress may accompany anxiety, depression, insomnia, immune system issues. This isn''t personal failure but requires professional support. If experiencing suicidal thoughts, immediately call mental health crisis hotline.", "zh-CN": "您目前处于极高的压力状态下，这可能严重影响您的身心健康、判断力和生活质量。立即关注压力管理至关重要。重要说明：极高压力状态可能伴随焦虑、抑郁、失眠、免疫系统下降等健康问题。这不是个人失败的标志，而是需要专业支持的信号。如果出现自杀念头，请立即拨打心理危机干预热线。", "de-DE": "Sie befinden sich in einem Zustand sehr hohen Stresses, der Ihre Gesundheit, Urteilsfähigkeit und Lebensqualität ernsthaft beeinträchtigen kann. Sofortige Aufmerksamkeit für Stressmanagement ist unerlässlich. Wichtiger Hinweis: Sehr hoher Stress kann mit Angstzuständen, Depressionen, Schlaflosigkeit und Problemen des Immunsystems einhergehen. Dies ist kein persönliches Versagen, sondern erfordert professionelle Unterstützung. Bei Selbstmordgedanken wenden Sie sich bitte sofort an eine Krisenhotline für psychische Gesundheit.", "fr-FR": "Vous êtes dans un état de stress très élevé qui peut sérieusement affecter votre santé, votre jugement et votre qualité de vie. Une attention immédiate à la gestion du stress est essentielle. Note importante : Un stress très élevé peut s''accompagner d''anxiété, de dépression, d''insomnie et de problèmes du système immunitaire. Ce n''est pas un échec personnel, mais cela nécessite un soutien professionnel. Si vous ressentez des pensées suicidaires, appelez immédiatement une ligne de crise en santé mentale.", "zh-TW": "您目前處於極高的壓力狀態下，這可能嚴重影響您的身心健康、判斷力和生活品質。立即關注壓力管理至關重要。重要說明：極高壓力狀態可能伴隨焦慮、抑鬱、失眠、免疫系統下降等健康問題。這不是個人失敗的標誌，而是需要專業支持的信號。如果出現自殺念頭，請立即撥打心理危機干預熱線。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('a56382a7-5eca-48c5-b491-c26b5eb60160', 'emotional_recognition', '2', '37', '52', '#FFA726', NOW(), NOW(), '{"zh-CN": {"肢体语言解读": 50, "语调情绪感知": 55, "面部表情识别": 60, "情绪变化敏感度": 65}, "en-US": {"Tone emotion perception": 55, "Body language interpretation": 50, "Facial expression recognition": 60, "Sensitivity to emotional changes": 65}, "de-DE": {"Tonemotionserkennung": 55, "Interpretation der Körpersprache": 50, "Erkennung von Gesichtsausdrücken": 60, "Empfindlichkeit gegenüber emotionalen Veränderungen": 65}, "fr-FR": {"Perception des émotions dans le ton": 55, "Interprétation du langage corporel": 50, "Reconnaissance des expressions faciales": 60, "Sensibilité aux changements émotionnels": 65}, "zh-TW": {"語調情緒感知": 55, "肢體語言解讀": 50, "面部表情識別": 60, "情緒變化敏感度": 65}}', '{"zh-CN": "发展水平", "en-US": "Developing Level", "de-DE": "Entwicklungsniveau", "fr-FR": "Niveau en développement", "zh-TW": "發展水平"}', '{"zh-CN": "您的情绪识别能力正在发展中，能够识别基本情绪，但对细微情绪变化和复杂情绪组合的识别仍有提升空间。", "en-US": "Your emotional recognition ability is developing. You can identify basic emotions but have room for improvement in recognizing subtle emotional changes and complex emotional combinations.", "de-DE": "Ihre Fähigkeit zur Emotionserkennung entwickelt sich. Sie können grundlegende Emotionen erkennen, haben jedoch Verbesserungspotenzial bei der Erkennung subtiler emotionaler Veränderungen und komplexer emotionaler Kombinationen.", "fr-FR": "Votre capacité de reconnaissance émotionnelle est en développement. Vous pouvez identifier les émotions de base mais avez des marges de progression dans la reconnaissance des changements émotionnels subtils et des combinaisons émotionnelles complexes.", "zh-TW": "您的情緒識別能力正在發展中，能夠識別基本情緒，但對細微情緒變化和複雜情緒組合的識別仍有提升空間。"}', '{"zh-CN": "您已经具备基础的情绪识别能力，但在复杂情境或细微情绪变化中可能遇到挑战。这可能影响您的人际沟通深度。重要说明：避免过度解读他人表情，特别是在缺乏上下文的情况下。情绪识别应与情境结合分析。", "en-US": "You possess foundational emotional recognition skills but may face challenges in complex situations or subtle emotional variations. This could affect the depth of your interpersonal communication. Important note: Avoid over-interpreting facial expressions, especially without context. Emotional recognition should be analyzed in combination with situational factors.", "de-DE": "Sie verfügen über grundlegende Fähigkeiten zur Emotionserkennung, könnten jedoch in komplexen Situationen oder bei subtilen emotionalen Variationen Herausforderungen erleben. Dies könnte die Tiefe Ihrer zwischenmenschlichen Kommunikation beeinträchtigen. Wichtiger Hinweis: Vermeiden Sie es, Gesichtsausdrücke überzuinterpretieren, insbesondere ohne Kontext. Emotionserkennung sollte in Kombination mit situativen Faktoren analysiert werden.", "fr-FR": "Vous possédez des compétences fondamentales en reconnaissance émotionnelle, mais vous pourriez rencontrer des défis dans des situations complexes ou des variations émotionnelles subtiles. Cela pourrait affecter la profondeur de votre communication interpersonnelle. Note importante : Évitez de surinterpréter les expressions faciales, surtout sans contexte. La reconnaissance émotionnelle doit être analysée en combinaison avec les facteurs situationnels.", "zh-TW": "您已經具備基礎的情緒識別能力，但在複雜情境或細微情緒變化中可能遇到挑戰。這可能影響您的人際溝通深度。重要說明：避免過度解讀他人表情，特別是在缺乏上下文的情況下。情緒識別應與情境結合分析。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('b87eca4c-bcb1-4c71-8049-2f0633300fee', 'emotional_recognition', '1', '20', '36', '#FF6B6B', NOW(), NOW(), '{"zh-CN": {"肢体语言解读": 35, "语调情绪感知": 40, "面部表情识别": 45, "情绪变化敏感度": 50}, "en-US": {"Tone emotion perception": 40, "Body language interpretation": 35, "Facial expression recognition": 45, "Sensitivity to emotional changes": 50}, "de-DE": {"Tonemotionserkennung": 40, "Interpretation der Körpersprache": 35, "Erkennung von Gesichtsausdrücken": 45, "Empfindlichkeit gegenüber emotionalen Veränderungen": 50}, "fr-FR": {"Perception des émotions dans le ton": 40, "Interprétation du langage corporel": 35, "Reconnaissance des expressions faciales": 45, "Sensibilité aux changements émotionnels": 50}, "zh-TW": {"語調情緒感知": 40, "肢體語言解讀": 35, "面部表情識別": 45, "情緒變化敏感度": 50}}', '{"zh-CN": "基础水平", "en-US": "Foundational Level", "de-DE": "Grundlegendes Niveau", "fr-FR": "Niveau fondamental", "zh-TW": "基礎水平"}', '{"zh-CN": "您目前在情绪识别方面处于发展阶段，可能对他人情绪变化的敏感度较低，有时会误解他人的非言语信号。", "en-US": "You are currently in the developmental stage of emotional recognition, with relatively low sensitivity to others'' emotional changes and occasional misinterpretations of nonverbal cues.", "de-DE": "Sie befinden sich derzeit in der Entwicklungsphase der emotionalen Erkennung, mit relativ geringer Sensibilität für emotionale Veränderungen anderer und gelegentlichen Fehlinterpretationen von nonverbalen Signalen.", "fr-FR": "Vous êtes actuellement en phase de développement de la reconnaissance émotionnelle, avec une sensibilité relativement faible aux changements émotionnels des autres et des interprétations erronées occasionnelles des signaux non verbaux.", "zh-TW": "您目前在情緒識別方面處於發展階段，可能對他人情緒變化的敏感度較低，有時會誤解他人的非語言信號。"}', '{"zh-CN": "我们注意到您在识别他人情绪方面还有较大的成长空间。这可能意味着您更多关注事情本身而非情绪氛围，或者对细微的表情、语调变化不够敏感。重要说明：情绪识别能力与文化背景、成长环境密切相关，无需因分数低而自责。部分职业（如技术、工程）对情绪识别要求较低，这是正常的多样性表现。", "en-US": "We notice you have significant room for growth in recognizing others'' emotions. This may mean you focus more on the content than emotional atmosphere, or are less sensitive to subtle facial expressions and tone variations. Important note: Emotional recognition ability is closely related to cultural background and upbringing. Don''t blame yourself for lower scores. Some professions (like technical or engineering fields) require less emotional recognition—this is normal diversity.", "de-DE": "Wir stellen fest, dass Sie erhebliches Wachstumspotenzial bei der Erkennung der Emotionen anderer haben. Dies könnte bedeuten, dass Sie sich mehr auf den Inhalt als auf die emotionale Atmosphäre konzentrieren oder weniger empfindlich gegenüber subtilen Gesichtsausdrücken und Tonvariationen sind. Wichtiger Hinweis: Die Fähigkeit zur emotionalen Erkennung hängt eng mit dem kulturellen Hintergrund und der Erziehung zusammen. Machen Sie sich keine Vorwürfe wegen niedrigerer Werte. Einige Berufe (wie technische oder ingenieurwissenschaftliche Bereiche) erfordern weniger emotionale Erkennung – dies ist normale Vielfalt.", "fr-FR": "Nous remarquons que vous avez une marge de progression significative dans la reconnaissance des émotions des autres. Cela peut signifier que vous vous concentrez davantage sur le contenu que sur l''atmosphère émotionnelle, ou que vous êtes moins sensible aux expressions faciales subtiles et aux variations de ton. Note importante : La capacité de reconnaissance émotionnelle est étroitement liée au contexte culturel et à l''éducation. Ne vous blâmez pas pour des scores plus faibles. Certains métiers (comme les domaines techniques ou d''ingénierie) nécessitent moins de reconnaissance émotionnelle - c''est une diversité normale.", "zh-TW": "我們注意到您在識別他人情緒方面還有較大的成長空間。這可能意味著您更多關注事情本身而非情緒氛圍，或者對細微的表情、語調變化不夠敏感。重要說明：情緒識別能力與文化背景、成長環境密切相關，無需因分數低而自責。部分職業（如技術、工程）對情緒識別要求較低，這是正常的多樣性表現。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('be107312-7bb0-4c48-8315-0ff0f52afc7e', 'leadership_potential', '2', '37', '52', '#FFA726', NOW(), NOW(), '{"en-US": {"Goal & execution": 55, "Team collaboration": 50, "Decision & ownership": 48, "Communication & influence": 52}, "zh-CN": {"团队协作": 50, "决策与担当": 48, "沟通影响力": 52, "目标与执行": 55}, "de-DE": {"Ziel & Umsetzung": 55, "Teamzusammenarbeit": 50, "Entscheidung & Verantwortung": 48, "Kommunikation & Einfluss": 52}, "fr-FR": {"Objectif & exécution": 55, "Collaboration d''équipe": 50, "Décision & responsabilité": 48, "Communication & influence": 52}, "zh-TW": {"目標與執行": 55, "團隊協作": 50, "決策與擔當": 48, "溝通影響力": 52}}', '{"en-US": "Developing Potential", "zh-CN": "发展中的潜力", "de-DE": "Entwicklungspotenzial", "fr-FR": "Potentiel en développement", "zh-TW": "發展中的潛力"}', '{"en-US": "You show developing leadership potential with some leadership qualities but may need more experience, training, or confidence.", "zh-CN": "您显示出发展中的领导潜力，具有一定的领导素质，但可能需要更多经验、培训或信心。", "de-DE": "Sie zeigen ein Entwicklungspotenzial in der Führung mit einigen Führungsqualitäten, benötigen jedoch möglicherweise mehr Erfahrung, Training oder Selbstvertrauen.", "fr-FR": "Vous montrez un potentiel de leadership en développement avec certaines qualités de leadership, mais vous pourriez avoir besoin de plus d''expérience, de formation ou de confiance.", "zh-TW": "您顯示出發展中的領導潛力，具有一定的領導素質，但可能需要更多經驗、培訓或信心。"}', '{"en-US": "You possess some leadership qualities but may not be fully developed or confident yet. Conscious cultivation and practice can significantly enhance your leadership ability. Important note: Leadership development is gradual process. Avoid prematurely taking responsibilities beyond current capabilities. Allow learning from mistakes.", "zh-CN": "您具备一些领导素质，但可能尚未完全发展或自信不足。有意识的培养和实践可以显著提升您的领导能力。重要说明：领导力发展是一个渐进过程。避免过早承担超出当前能力的责任。允许自己从错误中学习。", "de-DE": "Sie besitzen einige Führungsqualitäten, sind jedoch möglicherweise noch nicht vollständig entwickelt oder selbstbewusst. Bewusste Kultivierung und Übung können Ihre Führungsfähigkeiten erheblich verbessern. Wichtiger Hinweis: Führungskräfteentwicklung ist ein schrittweiser Prozess. Vermeiden Sie es, vorzeitig Verantwortung zu übernehmen, die über Ihre aktuellen Fähigkeiten hinausgeht. Lernen Sie aus Fehlern.", "fr-FR": "Vous possédez certaines qualités de leadership mais elles ne sont peut-être pas encore pleinement développées ou assurées. Une cultivation consciente et une pratique peuvent améliorer considérablement vos capacités de leadership. Note importante : Le développement du leadership est un processus graduel. Évitez de prendre des responsabilités prématurées au-delà de vos capacités actuelles. Apprenez de vos erreurs.", "zh-TW": "您具備一些領導素質，但可能尚未完全發展或自信不足。有意識的培養和實踐可以顯著提升您的領導能力。重要說明：領導力發展是一個漸進過程。避免過早承擔超出當前能力的責任。允許自己從錯誤中學習。"}');
INSERT INTO scale_scoring_rules (id, test_type, level, score_min, score_max, color, created_at, updated_at, ability_dimensions, label, interpretation, feedback) VALUES ('d10e600b-cec2-44e8-8c9e-e4887ab6bbed', 'psychological_resilience', '5', '85', '100', '#8A2BE2', NOW(), NOW(), '{"zh-CN": {"情绪调节": 95, "认知灵活性": 90, "社会支持运用": 88, "适应性问题解决": 92}, "en-US": {"Emotional regulation": 95, "Cognitive flexibility": 90, "Social support utilization": 88, "Problem-solving adaptability": 92}, "de-DE": {"Emotionale Regulierung": 95, "Kognitive Flexibilität": 90, "Nutzung sozialer Unterstützung": 88, "Anpassungsfähigkeit bei Problemlösungen": 92}, "fr-FR": {"Régulation émotionnelle": 95, "Flexibilité cognitive": 90, "Utilisation du soutien social": 88, "Adaptabilité à la résolution de problèmes": 92}, "zh-TW": {"情緒調節": 95, "認知靈活性": 90, "社會支持運用": 88, "適應性問題解決": 92}}', '{"zh-CN": "极高心理韧性", "en-US": "Very High Resilience", "de-DE": "Sehr hohe Resilienz", "fr-FR": "Résilience très élevée", "zh-TW": "極高心理韌性"}', '{"zh-CN": "您具有非常高的心理韧性，面对重大逆境时仍能保持功能、从中学习并成长，具备显著的抗逆力。", "en-US": "You have very high psychological resilience, maintaining function, learning and growing from significant adversity, with remarkable stress resistance.", "de-DE": "Sie verfügen über eine sehr hohe psychologische Resilienz, bewahren Ihre Funktionalität, lernen und wachsen aus erheblichen Widrigkeiten und zeigen bemerkenswerte Stressresistenz.", "fr-FR": "Vous avez une résilience psychologique très élevée, maintenant vos fonctions, apprenant et grandissant face à des adversités importantes, avec une résistance au stress remarquable.", "zh-TW": "您具有非常高的心理韌性，面對重大逆境時仍能保持功能、從中學習並成長，具備顯著的抗逆力。"}', '{"zh-CN": "您的心理韧性非常突出，可能使您在危机、变革或高压环境中表现出色。这种能力是宝贵的个人资源，也可用于服务他人和社会。重要说明：极高的心理韧性有时可能使您忽视脆弱的情感或低估他人的痛苦。注意保持同理心和对他人脆弱性的尊重。", "en-US": "Your psychological resilience is outstanding, potentially enabling exceptional performance in crises, change, or high-pressure environments. This ability is valuable personal resource for serving others and society. Important note: Very high resilience may sometimes cause overlooking vulnerable emotions or underestimating others'' suffering. Maintain empathy and respect for others'' vulnerability.", "de-DE": "Ihre psychologische Resilienz ist herausragend und ermöglicht möglicherweise außergewöhnliche Leistungen in Krisen, Veränderungen oder Hochdruckumgebungen. Diese Fähigkeit ist eine wertvolle persönliche Ressource, die auch anderen und der Gesellschaft dienen kann. Wichtiger Hinweis: Sehr hohe Resilienz kann manchmal dazu führen, dass verletzliche Emotionen übersehen oder das Leiden anderer unterschätzt wird. Bewahren Sie Empathie und Respekt für die Verletzlichkeit anderer.", "fr-FR": "Votre résilience psychologique est exceptionnelle, vous permettant potentiellement de réaliser des performances remarquables en période de crise, de changement ou dans des environnements sous haute pression. Cette capacité est une ressource personnelle précieuse pour servir les autres et la société. Note importante : Une résilience très élevée peut parfois entraîner une négligence des émotions vulnérables ou une sous-estimation des souffrances des autres. Maintenez l''empathie et le respect pour la vulnérabilité des autres.", "zh-TW": "您的心理韌性非常突出，可能使您在危機、變革或高壓環境中表現出色。這種能力是寶貴的個人資源，也可用於服務他人和社會。重要說明：極高的心理韌性有時可能使您忽視脆弱的情感或低估他人的痛苦。注意保持同理心和對他人脆弱性的尊重。"}');
